INSERT INTO `jos_content` VALUES(62, 'Ամանորյա միջոցառում', '2008-11-24-08-47-03', '', '<p><img src="images/stories/snowglobe_3d_screensaver_215.gif" border="0" hspace="10" vspace="10" align="left" /><strong>Հարգելի գործընկերներ,</strong></p><p>ՀՀ դատավորների միությունը դեկտեմբերի 26-ին ժամը 18<u><sup>00</sup></u>-ին &lt;&lt;Արքայաձոր&gt;&gt; ռեստորանային համալիրում (հասցե` ք. Երևան, Մյասնիկյան պող. 32) կազմակերպում է ամանորյա միջոցառում:</p><p>Միջոցառմանը կարող են մասնակցել բոլոր դատավորները, ինչպես նաև նրանց ընտանիքի անդամները, անհրաժեշտ է միայն ՀՀ դատավորների միությանը նախապես տեղեկացնել մասնակցության մասին և յուրաքանչյուր անձի համար վճարել 20.000 ՀՀ դրամ մինչև 2008 թվականի դեկտեմբերի 5-ը: </p>', '', -2, 1, 0, 3, '2008-11-24 08:36:14', 64, '', '2008-11-26 09:21:01', 64, 0, '0000-00-00 00:00:00', '2008-11-24 00:00:00', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 7, 0, 0, '', '', 0, 50, 'robots=\nauthor=');
