INSERT INTO `jos_content` VALUES(56, '51-st Annual Meeting of IAJ took place in Yerevan', '51-st-annual-meeting-of-iaj-took-place-in-yerevan', '', '<span><font size="3"><font color="#000000"><font face="Calibri">Due to charter of IAJ, during Annual Meeting jointly with other questions is discussing the place selection of posterior Annual Meeting. In Norway Armenian Association of Judges <span> </span>was elected <span> </span>as a hosting Association.</font></font></font></span><span><font size="3"><font color="#000000"><font face="Calibri">In September 7<sup>th</sup>-11<sup>th</sup>, 2008, first time in the region and in the territory of CIS took place 51<sup>st</sup> Annual Meeting of IAJ, which participated more than 216 participants from 53 countries.</font></font></font></span><span><font size="3"><font color="#000000"><font face="Calibri"> ', '<p><span><font size="3"><font color="#000000"><font face="Calibri">The opening ceremony of 51<sup>st</sup> Annual Meeting consisted at 8<sup>th</sup> of September, during which developed testimonials Chief Justice of Cassation Court Hovhannes Manoukyan, President of IAJ Maja Tratnik, also Chairman of Armenian Association of Judges Hrachik Sargsyan.</font></font></font></span><span><font size="3"><font color="#000000"><font face="Calibri">At 7<sup>th</sup> of September consisted sessions of Ibero-American, African, Asian, North-American and Oceanic Regional Groups, also session of the European Association.</font></font></font></span><span><font size="3"><font color="#000000"><font face="Calibri">In the Evening guests visited Museum-Institute of the Armenian Genocide.</font></font></font></span><span><font size="3"><font color="#000000"><font face="Calibri">Reminding, that in November, 2005 in Montevideo the capital of Uruguay during 48<sup>th</sup> Annual Meeting of IAJ Armenian Association of Judges became the 68<sup>th</sup> member of IAJ.</font></font></font></span><span><font size="3"><font color="#000000"><font face="Calibri">In September , 2007, in Trondheim had taken place 50<sup>th</sup> Annual Meeting of IAJ, which participated also delegates of Armenian Association of Judges of the RA.</font></font></font></span></p></font></font></font></span>', -2, 1, 0, 35, '2008-09-19 10:39:05', 64, '', '2008-09-19 11:11:19', 64, 0, '0000-00-00 00:00:00', '2008-09-19 00:00:00', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 0, '', '', 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES(60, '51st  Annual Meeting of IAJ took place in Yerevan', '51st-annual-meeting-of-iaj-took-place-in-yerevan', '', 'In November , 2005, in Montevideo the capital of Uruguay, during 48th Annual Meeting of IAJ Armenian Association of Judges became the 68th member of IAJ.<br />In September, 2007, in Trondheim had taken place 50th Annual Meeting of IAJ, which participated delegates of Armenian Association of Judges of the RA.<br />During 50th Annual Meeting of IAJ in 2007 in Norway Armenian Association of Judges was elected as a hosting Association of 51st Annual Meeting of 2008.<br />In September 7th-11th, 2008 first time in the region and in the territory of CIS took place', '', -2, 1, 0, 35, '2008-09-22 13:03:33', 64, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2008-09-22 13:03:33', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 0, '', '', 0, 0, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES(61, '51st  Annual Meeting of IAJ took place in Yerevan', '51st-annual-meeting-of-iaj-took-place-in-yerevan', '', '<p>In November , 2005, in Montevideo the capital of Uruguay, during 48th Annual Meeting of IAJ Armenian Association of Judges became the 68th member of IAJ.<br />In September, 2007, in Trondheim had taken place 50th Annual Meeting of IAJ, which participated delegates of Armenian Association of Judges of the RA.</p>', '<p><br />During 50th Annual Meeting of IAJ in 2007 in Norway Armenian Association of Judges was elected as a hosting Association of 51st Annual Meeting of 2008.<br />In September 7th-11th, 2008 first time in the region and in the territory of CIS took place 51st Annual Meeting of IAJ, in which participated more than 216 participants from 53 countries.</p>', 1, 1, 0, 35, '2008-09-23 06:46:14', 64, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2008-09-23 06:46:14', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 183, '', '', 0, 5801, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES(69, 'ADVERTISEMENT', 'advertisement', '', 'Armenian Association of Judges of the RA confirms, that in September 14-17, 2009 will take place annual meeting of the European Association of Judges in Cracow. ', '<br />The meetings will take place at the Campanile Hotel, a fine conference hotel located in the centre of Cracow, 45 minutes by bus from Balice Airport. The sufficient numbers of rooms has been reserved for the delegates. Although national associations are kindly request to send preferably one delegate (no more than two). The hotel has an international standard kitchen and can accommodate special dietary needs.<br />Also in 2009 will take place 52-nd Annual Meeting of the International association of Judges in Marrakech.<br />For further information please contact us     (+37410) 569979<br />                                                             (+37410) 569989', -2, 1, 0, 35, '2009-02-17 12:37:49', 64, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2009-02-17 12:37:49', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 0, '', '', 0, 0, 'robots=\nauthor=');
