INSERT INTO `jos_content` VALUES(67, 'FREEDOM OF THOUGHT, CONSCIENCE AND RELIGION', 'freedom-of-thought-conscience-and-religion', '', '<p><strong><img src="images/stories/mtqi.jpg" border="0" align="right" /><a href="images/Publications/mtq.pdf">"ՄՏՔԻ, ԽՂՃԻ ԵՎ ԿՐՈՆԻ ԱԶԱՏՈՒԹՅՈՒՆ"</a></strong></p><p>Գիրքը հրատարակվել է Եվրոպական հանձնաժողովի և Եվրոպայի խորհրդի "Մարդու իրավունքների մշակույթի խթանման" համատեղ ծրագրի միջոցներով մարդու իրավունքների եվրոպական մշակույթը Հարավային Կովկասի երկրներում և Ուկրաինայում զարգացնելու նպատակով:</p><p>Մարդու իրավունքների եվրոպական կոնվենցիայի իրականացման ուղեցույց</p><p>Ջիմ Մուրդոք</p><p>Խմբագիր` <strong>Վահե Ենգիբարյան</strong></p><p>               ՀՀ դատավորների միության գործադիր տնօրեն, ԵՊՀ իրավագիտության ֆակուլտետի դոցենտ, իրավագիտության թեկնածու</p><p>&nbsp;</p><p>PUBLICATION IS FINANCED BY THE EUROPEAN COMMISSION AND THE COUNCIL OF EUROPE  JOINT PROGRAMME<br />Edited by <strong>Vahe YENGIBARYAN</strong><br />Executive Director of the Association of Judges of the Republic of Armenia, Professor of YSU Law Faculty, PhD in Law</p>', '', 1, 1, 0, 36, '2008-12-22 09:30:49', 64, '', '2009-04-14 06:35:43', 64, 0, '0000-00-00 00:00:00', '2008-12-22 09:30:49', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 6, 0, 59, '', '', 0, 4713, 'robots=\nauthor=');
