INSERT INTO `jos_content` VALUES(119, 'ԴԱՏԱՎՈՐՆԵՐԻ ՄԻՋԱԶԳԱՅԻՆ ՄԻՈՒԹՅԱՆ ՏԱՐԵԿԱՆ 51-ՐԴ ՀԱՄԱԺՈՂՈՎ', '-51-', '', '<p><font color="#000000"><a href="html/images/Publications/ABA%20arm.pdf">ԴԱՏԱՎՈՐՆԵՐԻ ՄԻՋԱԶԳԱՅԻՆ ՄԻՈՒԹՅԱՆ ՏԱՐԵԿԱՆ 51-ՐԴ ՀԱՄԱԺՈՂՈՎ</a></font> </p><p>ՀՀ դատավորների միությունը Ամերիկյան իրավաբանների ընկերակցության, CEELI Inc.-ի հայաստանյան ներկայացուցչության օժանդակությամբ տպագրել է <img src="images/stories/aba-1.jpg" border="0" hspace="15" vspace="15" align="right" />հուշագիր Դատավորների միջազգային միության 51-րդ տարեկան համաժողովի վերաբերյալ:</p><p>Խմբագիրներ`<br />Հրաչիկ ՍԱՐԳՍՅԱՆ - ՀՀ դատավորների միության նախագահ, Արմավիրի մարզի ընդհանուր իրավասության դատարանի նախագահ,</p><p>Վահե ԵՆԳԻԲԱՐՅԱՆ - ՀՀ դատավորների միության գործադիր տնօրեն, ԵՊՀ իրավագիտության ֆակուլտետի դոցենտ, իրավագիտության թեկնածու,</p><p>Թարգմանիչ`  Արմինե ՆԱԶԱՐՅԱՆ - ՀՀ դատավորների միության գործադիր տնօրենի օգնական,</p><p>Համակարգչային ձևավորումը` Ամալյա ՀԱՐՈՒԹՅՈՒՆՅԱՆԻ - ՀՀ դատավորների միության գործադիր տնօրենի օգնական:</p><p>&nbsp;</p>', '', 1, 1, 0, 36, '2009-08-04 12:52:23', 64, '', '2009-09-11 09:57:30', 64, 0, '0000-00-00 00:00:00', '2009-08-04 12:52:23', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=hy-AM\nkeyref=\nreadmore=', 4, 0, 50, '', '', 0, 5774, 'robots=\nauthor=');
