INSERT INTO `jos_content` VALUES(77, 'ՀՀ ԴԱՏԱՎՈՐՆԵՐԻ ՄԻՈՒԹՅԱՆԴԻՄՈՒՄ', '2009-03-10-08-25-43', '', '<p><strong>ՀԱՅԱՍՏԱՆԻ ՀԱՆՐԱՊԵՏՈՒԹՅԱՆ <br />ԴԱՏԱՎՈՐՆԵՐԻ ՄԻՈՒԹՅԱՆՆ ԱՆԴԱՄԱԳՐՎԵԼՈՒ <br />ԴԻՄՈՒՄ</strong></p>', '<p>&nbsp;</p><p>ՀԱՅԱՍՏԱՆԻ ՀԱՆՐԱՊԵՏՈՒԹՅԱՆ <br />ԴԱՏԱՎՈՐՆԵՐԻ ՄԻՈՒԹՅԱՆ ԽՈՐՀՐԴԻՆ</p><p>Անուն, հայրանուն, ազգանուն ………………………………………………………………………….<br />……………………………………………………………………………………………………………….<br />Ծննդյան օրը, ամիսը, տարեթիվը……………………………………………………………………….<br />Դատարանի անվանումը………………………………………………………………………………....<br />Պաշտոնը և նշանակման տարեթիվը………………………………………………………………<br />Գիտական աստիճանը……………………………………………………………………………………<br />Օտար լեզվի տիրապետումը……………………………………………………………………………<br />Ընտանեկան դրությունը…………………………………………………………………………………<br />Խնամքին գտնվող անձանց թիվը………………………………………………………………………<br />Դատարանի հասցեն և հեռախոսի համարը…………………………………………………………</p><p>………………………………………………………………………………………………………………<br />Բնակավայրի հասցեն և հեռախոսի համարը…………………………………………………………<br />………………………………………………………………………………………………………………<br />Այլ տվյալներ………………………………………………………………………………………………<br />………………………………………………………………………………………………………………<br />Անձնական ստորագրություն…………………………………………………………………………</p><p> Սույն դիմումի ձևը լրացրած վիճակում միության խորհուրդ ներկայացնող դատավորը համարվում է Հայաստանի Հանրապետության դատավորների միության անդամ:</p><p>     Դիմումն ընդունված է  "……"………………200   թ.</p><p>Միության նախագահ………………………………………………………………………………  (ստորագրություն)              </p>', 1, 1, 0, 35, '2009-03-10 08:22:39', 64, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2009-03-10 08:22:39', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 162, '', '', 0, 5091, 'robots=\nauthor=');
