INSERT INTO `jos_content` VALUES(88, 'ԿՈՆՖԵՐԱՆՍԻ ՆԱԽՆԱԿԱՆ ԾՐԱԳԻՐ', '2009-04-08-12-50-23', '', '<strong>2009թ. հունիսի 19 Վիեննա </strong><strong>', '<p>09.30 Պատվիրակների գրանցում<br />10.00 Ներկայացում և ամփոփիչ քննարկում.<br />• տեխնիկական նախադրյալներ (հեռախոսային, ինտերնետային),<br />• իրավական սահմանափակումներ` հսկողություն և մարդու հիմնարար իրավունքներ,<br />• հսկողության, փաստացի պաշտպանության և Եվրոպական միության գործակալության դերը մարդու հիմնարար իրավունքների համատեքստում:<br />13.00 Ընդմիջում (ճաշ)<br />14.15 Քննարկումներ հետևյալ թեմաներով<br />1. Վեճերի և մարդու հիմնարար իրավունքների պաշտպանություն:<br />2. Հսկողության ինտերակտիվ և այլ ձևեր – մասնավոր կյանքին միջամտում:<br />3. Եվրոպական և ազգային կառուցակարգեր – դրանց դերը մարդու հիմնարար իրավունքների պաշտպանության գործում:<br />4. Մարդու հիմնարար իրավունքների պաշտպանության ինստիտուցիոնալ չափորոշիչները – դատական իշխանության դերը:<br />16.00 Ընդմիջում (սուրճ)<br />16.30 Եզրափակում և ամփոփիչ քննարկում<br />17.30 Կոնֆերանսի ավարտ</p></strong>', 1, 1, 0, 35, '2009-04-08 12:45:52', 64, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2009-04-08 12:45:52', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 169, '', '', 0, 5427, 'robots=\nauthor=');
