INSERT INTO `jos_content` VALUES(90, 'TERMS OF REFERENCE FOR AN INTERNATIONAL CONSULTANT', 'terms-of-reference-for-an-international-consultant', '', '<span></span><span><font face="Times New Roman" size="3" color="#000000"><span></span></font></span><span></span><span><font size="3"><font color="#000000"><font face="Times New Roman">Post Title: <span>                   </span><span> </span><span>          </span>International Consultant </font></font></font></span><span><font size="3"><font color="#000000"><font face="Times New Roman">Description:<span>                           </span>Training courses on international human rights standards during </font></font></font></span><span><font size="3"><font color="#000000"><font face="Times New Roman">criminal proceedings</font></font></font></span><font size="3"><font color="#000000"><font face="Times New Roman"><span>Dates:<span>  </span><span>                                   </span>2</span><span>0 to 31 July (both dates inclusive) </span></font></font></font><font size="3"><font color="#000000"><font face="Times New Roman"><span>Location:<span>  </span><span>                              </span></span><span>Dashoguz, Turkmenabat, Mary, Turkmenbashy and Ashgabat (Turkmenistan)</span><span></span></font></font></font><span><font size="3"><font color="#000000"><font face="Times New Roman">Type of Contract: <span>                  </span>Special Services Agreement </font></font></font></span><span><font size="3"><font color="#000000"><font face="Times New Roman">Deadline for applications: <span>     </span>15 May 2009</font></font></font></span><span><font face="Times New Roman" size="3" color="#000000"> </font></span><span><font face="Times New Roman" size="3" color="#000000"> </font></span><font size="3"><font color="#000000"><font face="Times New Roman"><span>The OSCE Centre in Ashgabat is looking for an International Expert to deliver five </span><span>training sessions on international human rights standards during criminal proceedings</span><span>. Each training session will last two-days and will target </span><span>judges, prosecutors, investigators and defense lawyers from Turkmenistan</span><span>. The five training sessions will be organized between the 20-31 July 2009</span><span> </span><span>in Dashoguz, Turkmenabat, Mary, Turkmenbashy and Ashgabat. Two experts will be involved in delivering the training courses. </span><span></span></font></font></font><span><font face="Times New Roman" size="3" color="#000000"> </font></span><span><font face="Times New Roman" size="3" color="#000000"> ', '</font></span><strong><span><font size="3"><font color="#000000"><font face="Times New Roman">Responsabilities</font></font></font></span></strong><strong><span><font face="Times New Roman" size="3" color="#000000"> </font></span></strong><span><font face="Times New Roman" size="3" color="#000000">The </font><a name="OLE_LINK2"></a><a name="OLE_LINK1"></a><span><font face="Times New Roman" size="3" color="#000000">International Expert </font></span><font size="3"><font color="#000000"><font face="Times New Roman">will be tasked to: </font></font></font></span><font color="#000000"><span style="font-family: Symbol"><span><font size="3">·</font><span style="font: 7pt ''Times New Roman''">         </span></span></span><font face="Times New Roman"><span><font size="3">Design and deliver five two-day </font></span><span><font size="3">training courses </font></span><span><font size="3">on International Human Rights Standards during Criminal Proceedings;</font></span></font></font><font color="#000000"><span style="font-family: Symbol"><span><font size="3">·</font><span style="font: 7pt ''Times New Roman''">         </span></span></span><span><font size="3"><font face="Times New Roman">Advice the Centre on possible materials to be disseminated during the training courses;</font></font></span></font><font color="#000000"><span style="font-family: Symbol"><span><font size="3">·</font><span style="font: 7pt ''Times New Roman''">         </span></span></span><span><font size="3"><font face="Times New Roman">Provide a report following the conclusion of the training courses which shall include an evaluation of the courses as well as conclusions and recommendations.</font></font></span></font><span><font face="Times New Roman" size="3" color="#000000"> </font></span><strong><span><font size="3"><font color="#000000"><font face="Times New Roman">Qualifications</font></font></font></span></strong><strong><span><font face="Times New Roman" size="3" color="#000000"> </font></span></strong><span><font size="3"><font color="#000000"><font face="Times New Roman">Education: Advanced university degree (Master’s degree or equivalent) in the field of International Law, Public Law, Criminal Law and Criminal Procedure, or other related field;</font></font></font></span><span><font face="Times New Roman" size="3" color="#000000"> </font></span><span><font size="3"><font color="#000000"><font face="Times New Roman">Work experience: Practicing (or former) judge, prosecutor or defense lawyer; At least ten years of progressively responsible experience in the criminal justice sector in general and particularly field of human rights and/or the international legal order with a focus on criminal justice; Previous working experience in a CIS country is highly desirable; </font></font></font></span><span><font face="Times New Roman" size="3" color="#000000"> </font></span><font size="3"><font face="Times New Roman"><span style="color: black">Languages: </span><span><font color="#000000">Fluency in written and spoken English; Fluency in Russian highly desirable.</font></span></font></font><span><font face="Times New Roman" size="3" color="#000000"> </font></span><font size="3"><font face="Times New Roman"><span><font color="#000000">Other skills: <span style="color: black">Excellent spoken communication skills, including the ability to convey complex legal concepts, in a clear, concise style.</span></font></span><span></span></font></font><span><font face="Times New Roman" size="3" color="#000000"> </font></span><span><font face="Times New Roman" size="3" color="#000000"> </font></span><span><font face="Times New Roman" size="3" color="#000000"> </font></span><span><font face="Times New Roman" size="3" color="#000000">Interested candidates are encouraged to send their C.V. and letter of interest via e-mail to: </font><a href="mailto:yavor.dinev@osce.org"><u><font face="Times New Roman" size="3" color="#0000ff">yavor.dinev@osce.org</font></u></a><font face="Times New Roman" size="3" color="#000000"> and </font><a href="mailto:sahragul.velbayramova@osce.org"><u><font face="Times New Roman" size="3" color="#0000ff">sahragul.velbayramova@osce.org</font></u></a></span><span><font face="Times New Roman" size="3" color="#000000"> </font></span><span><font face="Times New Roman" size="3" color="#000000"> </font></span><span><font face="Times New Roman" size="3" color="#000000"> </font></span>', 1, 1, 0, 35, '2009-04-08 13:07:46', 64, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2009-04-08 13:07:46', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 167, '', '', 0, 5303, 'robots=\nauthor=');
