INSERT INTO `jos_content` VALUES(93, 'ԱՆԱՏՈԼԻ ՄԱԹԵՎՈՍՅԱՆԸ` ՀՀ ՔԱՂԱՔԱՑԻԱԿԱՆ ՎԵՐԱՔՆՆԻՉ ԴԱՏԱՐԱՆԻ ՆԱԽԱԳԱՀ', '2009-04-09-09-09-06', '', '<div align="justify" style="padding-right: 134px; margin-top: 20px; margin-bottom: 35px">Ղեկավարվելով Հայաստանի Հանրապետության Սահմանադրության 55-րդ հոդվածի 11-րդ կետով և հիմք ընդունելով Արդարադատության խորհրդի 2009 թվականի ապրիլի 3-ի առաջարկությունը` ՀՀ նախագահ Սերժ Սարգսյանը որոշել է Անատոլի ՄԱԹԵՎՈՍՅԱՆԻՆ նշանակել Հայաստանի Հանրապետության քաղաքացիական վերաքննիչ դատարանի նախագահ` դադարեցնելով նրա` Հայաստանի Հանրապետության վճռաբեկ դատարանի քաղաքացիական և վարչական պալատի դատավորի լիազորությունները:</div>', '', 1, 1, 0, 35, '2009-04-09 09:04:42', 64, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2009-04-09 09:04:42', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 164, '', '', 0, 5189, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES(134, 'ՀՀ ՆԱԽԱԳԱՀԻ ՀՐԱՄԱՆԱԳԻՐ', '2010-01-14-11-26-06', '', '<p align="justify">    Հայաստանի Հանրապետության նախագահը, ղեկավարվելով Հայաստանի Հանրապետության Սահմանադրության 55-րդ հոդվածի 11-րդ կետով և հիմք ընդունելով Արդարադատության խորհրդի 2009 թվականի դեկտեմբերի 24-ի եզրակացությունը, իր 2009 թվականի դեկտեմբերի 30-ի հրամանագրով որոշել է Վլադիմիր ԳՐԻԳՈՐՅԱՆԻՆ նշանակել Երևան քաղաքի Էրեբունի և Նուբարաշեն վարչական շրջանների ընդհանուր իրավասության դատարանի դատավոր:</p>', '', 1, 1, 0, 35, '2010-01-14 11:16:37', 64, '', '0000-00-00 00:00:00', 0, 64, '2010-12-24 07:03:57', '2010-01-14 11:16:37', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=hy-AM\nkeyref=\nreadmore=', 1, 0, 151, '', '', 0, 4102, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES(94, 'ԿԱՅԱՑԱՎ ԴԱՏԱՐԱՆՆԵՐԻ ՆԱԽԱԳԱՀՆԵՐԻ ԽՈՐՀՐԴԻ ՆԻՍՏ', '2009-04-14-07-08-42', '', '<p align="justify"><strong><em>Ապրիլի 10-ին տեղի ունեցավ Հայաստանի Հանրապետության դատարանների նախագահների խորհրդի նիստ։ <br /></em></strong>Նիստի սկզբում ՀՀ դատական դեպարտամենտի ղեկավարը Դատարանների նախագահների խորհրդին ներկայացրեց 2008 թվականին կատարված աշխատանքների վերաբերյալ հաշվետվությունը։ <br />Նիստի ընթացքում փոփոխություններ կատարվեցին “ՀՀ դատարանների նախագահների խորհրդի հանձնաժողովներ ձևավորելու, հանձնաժողովների կազմը սահմանելու մասին”, “ՀՀ դատական դեպարտամենտի պաշտոնական գրությունների ձևաթղթերը հաստատելու մասին”, “ՀՀ դատական դեպարտամենտի փաստաթղթերի ելքի համարակալումները սահմանելու մասին”, “Դատավորների կնիքների և դրոշմակնիքների գծապատկերները հաստատելու մասին”, ինչպես նաև “ՀՀ դատարանների գործավարության կանոնները հաստատելու մասին” որոշումներում։ <br />Բացի այդ, Խորհուրդն ընդունեց “ՀՀ առաջին ատյանի դատարանների նստավայրերը, դատավորների՝ ըստ նստավայրերի բաշխման և մասնագիտացման ցանկը հաստատելու մասին” որոշումը, որի 2-րդ կետով ուժը կորցրած ճանաչեց “ՀՀ դատարանների օրենքով չսահմանված նստավայրեր սահմանելու և դատավորների՝ ըստ նստավայրերի բաշխման կարգը հաստատելու մասին” թիվ 25Լ որոշումը։ <br />Խորհրդի նիստում քննարկվեցին նաև կազմակերպական բնույթի հարցեր, ինչպես նաև որոշվեց ստեղծել աշխատանքային խումբ ”Հայաստանի Հանրապետության առաջին ատյանի ընդհանուր իրավասության դատարաններում դատավորների միջև գործերի բաշխման կարգը հաստատելու մասին” որոշման վերջնական տեքստը խմբագրելու նպատակով։</p>', '', 1, 1, 0, 35, '2009-04-14 07:07:32', 64, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2009-04-14 07:07:32', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 163, '', '', 0, 5309, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES(95, 'ՎԱՐԴԱՆ  ԱՎԱՆԵՍՅԱՆԸ` ՀՀ ՎՃՌԱԲԵԿ ԴԱՏԱՐԱՆԻ ԴԱՏԱՎՈՐ', '2009-04-14-13-48-59', '', '<p><img src="images/stories/avanesyan20v_.jpg" border="0" hspace="15" vspace="15" align="left" />Ապրիլի 14-ին տեղի ունեցավ Արդարադատության խորհրդի նիստ, որի ընթացքում Խորհուրդը, քննարկելով Հայաստանի Հանրապետության վճռաբեկ դատարանի քաղաքացիական և վարչական պալատի դատավորի պաշտոնում նշանակելու համար Հայաստանի Հանրապետության վերաքննիչ քաղաքացիական դատարանի դատավոր Վարդան Ավանեսյանի թեկնածությունն առաջարկելու վերաբերյալ հարցը և ղեկավարվելով Հայաստանի Հանրապետության Սահմանադրության 95-րդ հոդվածի 3-րդ կետով, Հայաստանի Հանրապետության դատական օրենսգրքի 147-րդ հոդվածի 2-րդ մասով, 149-րդ հոդվածով և 111-րդ հոդվածի 1-ին մասով, որոշեց Հայաստանի Հանրապետության նախագահին առաջարկել Վարդան ԱՎԱՆԵՍՅԱՆԻ թեկնածությունը՝ Հայաստանի Հանրապետության վճռաբեկ դատարանի քաղաքացիական և վարչական պալատի դատավորի պաշտոնում նշանակելու համար՝ ազատելով նրան Հայաստանի Հանրապետության վերաքննիչ քաղաքացիական դատարանի դատավորի պաշտոնից։</p>', '', -2, 1, 0, 35, '2009-04-14 13:45:33', 64, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2009-04-14 13:45:33', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 0, '', '', 0, 73, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES(96, 'Meeting with the Council of Europe and the Delagation of European Union', '2009-04-16-11-53-37', '', 'On 15th of April President of the Association of Judges of the RA Hrachik Sargsyan met with the joint delegation of  Council of Europe and European Union. <br />Members of the joint delegation Erik Svanidze and Artashes Melikyan as well as the Executive Director of the Association of Judges of the RA Vahe Yengibaryan attended the meeting.<br />During the Meeting all participants highly appreciated the results of the cooperation between Association of Judges of the RA and Council of Europe for over than 8 years. The agenda of the discussion was to make closer cooperation between the Association and Council of Europe and the European Union for fulfilling joint programs, particularly dealing trainings for the judges of the RA concerning  the Article 3 of the European Convention, also activities on notion and introduction of the case-law practice of ECHR in this area  has been considered.', '', -2, 1, 0, 35, '2009-04-16 11:50:42', 64, '', '2009-05-22 00:38:47', 62, 0, '0000-00-00 00:00:00', '2009-04-16 11:50:42', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 4, 0, 0, '', '', 0, 65, 'robots=\nauthor=');
