INSERT INTO `jos_content` VALUES(148, 'ՇՆՈՐՀԱՎՈՐ ՄԱՐՏԻ 8', '-8', '', '<img src="images/stories/ts.jpg" border="0" hspace="15" vspace="15" align="left" />ՀՀ դատավորների միությունը շնորհավորում է կին դատավորների, ինչպես նաև մայրերի ու քույրերի մարտի 8-ը, մաղթում առողջություն, կանացի անսպառ հմայք, անսահման սեր, անձնական և ընտանեկան երջանկություն:', '', 1, 1, 0, 35, '2010-03-05 11:45:47', 64, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2010-03-05 11:45:47', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=hy-AM\nkeyref=\nreadmore=', 1, 0, 138, '', '', 1, 826, 'robots=\nauthor=');
