INSERT INTO `jos_content` VALUES(164, 'Միջազգային սիմպոզիում', '2010-07-01-11-31-09', '', '<p>Միջազգային սիմպոզիում<br />Հուլիսի 17-19, 2010թ.</p><p><br />Տվյալ սիմպոզիումը անցկացվում է եվրոպական միությունների և կազմակերպությունների միջև հետագա համագործակցության ընդլայնման նպատակով: <br />Միջազգային սիմպոզիումի նպատակն է Եվրոպայի ներսում արդարադատության ոլորտում ազգային միությունների միջև տեսակետների և փորձի փոխանակումը, ինչպես նաև դատական բարեփոխումների հետևանքների վերաբերյալ քննարկումների անցկացումը:  Այն կոչված է հիմնելու այն սկզբունքները, որոնցով անհատները մասնակից կդառնան Եվրոպայում և Եվրոպային համար արդարադատության ոլորտի բարեփոխումներին:  <br />Մասնակցության վճարը 120 եվրո է, այն իր մեջ ներառում է հյուրանոցի, սննդի, ինչպես նաև ծրագրով նախատեսված ծախսերը: <br />Մանրամասների համար դիմել ՀՀ դատավորների միություն`<br />Հեռ: (+37410) 569979</p>', '', 1, 1, 0, 35, '2010-07-01 11:29:47', 64, '', '0000-00-00 00:00:00', 0, 64, '2010-07-01 11:31:11', '2010-07-01 11:29:47', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 129, '', '', 0, 3974, 'robots=\nauthor=');
