INSERT INTO `jos_content` VALUES(175, 'ԿԵՆՍԱԹՈՇԱԿ` ԿԱՆԽԻԿ ԵՎ ԱՆԿԱՆԽԻԿ ԵՂԱՆԱԿՈՎ', '2010-11-04-10-34-51', '', '<p align="justify">    <strong>Այսօր կառավարության նիստում հաստատվեց կենսաթոշակների վճարման կարգը: Ճեպազրույցի ընթացքում ՀՀ աշխատանքի և սոցիալական հարցերի նախարար Արթուր Գրիգորյանը մանրամասնեց, որ կենսաթոշակների վճարման կարգը պայմանավորված է կենսաթոշակային բարեփոխումների գործընթացով: Որոշում է կայացվել, որ այսուհետ կենսաթոշակի վճարման կարգը պետք է կատարվի երկու եղանակով` կանխիկ և անկանխիկ:</strong> </p>', '<p align="justify">Անկանխիկ եղանակով կենսաթոշակառուի կենսաթոշակը վճարվելու է նրա բանկային հաշվին փոխանցելու միջոցով: Սա հնարավորություն կտա կենսաթոշակառուին իր բանկային հաշվին փոխանցված կենսաթոշակային գումարը տնօրինել առանց որևէ ժամկետի սահմանափակման: Ըստ այդմ` կառավարությունն առաջարկում է բանկերին նախարարության հետ կնքել պայմանագիր և հնարավորություն տալ կենսաթոշակառուներին մատուցել ծառայություններ, որոնց համար անհրաժեշտ կլինի բանկի և կենսաթոշակառուի միջև կնքել համապատասխան պայմանագիր: <br />   Կենսաթոշակառուն պետք է այցելի նախարարության հետ պայմանագիր կնքած բանկ և բացի բանկային հաշիվ: Ընդ որում, բանկային հաշվի բացումը և սպասարկումը տրամադրվում է անվճար. ծախսերը հոգալու է պետությունը: Այնուհետև կենսաթոշակառուն պետք է այցելի իր բնակության վայրի կենսաթոշակ նշանակող տարածքային բաժին և ներկայացնի համապատասխան դիմում` նշելով բանկի անվանումը և իր բանկային հաշվի համարը: Այսինքն` կենսաթոշակառուն ինքն է ընտրում, թե որ բանկից ստանա իր կենսաթոշակը: <br />   Համապատասխան բանկեր պայմանագրեր կկնքեն լիազոր մարմնի հետ, տվյալ պարագայում` Աշխատանքի և սոցիալական հարցերի նախարարության հետ, և կընտրվի բանկը: Ընտրված բանկերի ցանկը կհրապարակվի նախարարության կայքէջի և ԶԼՄ-ների միջոցով: Կենսաթոշակառուն ազատ է իր ընտրության հարցում. նա կարող է ընտրել ցանկացած բանկ և իր կենսաթոշակն ստանալ այդ բանկից` քարտերի կամ բանկ հաճախելու միջոցով:<br />   Կանխիկ եղանակն ընտրած կենսաթոշակառուի կենսաթոշակը շարունակվելու է վճարվել գործող կարգով: Կենսաթոշակային գումարը կենսաթոշակառուին իր բնակարանում կամ համապատասխան վճարային գործակալությունում առձեռն վճարելու միջոցով: Այս բնագավառում ևս բոլոր այն վճարահաշվարկային կազմակերպությունները, որոնք նախարարության հետ կկնքեն պայմանագիր, կարող են քաղաքացիներին մատուցել կենսաթոշակի վճարման ծառայություն: <br />   Կանխիկ եղանակով վճարումը ներկայումս իրականացվում է. առանձնապես փոփոխություններ չկան այս բնագավառում: Թոշակառուն իր թոշակը պետք է ստանա կանխիկ եղանակով փոստային ծառայության աշխատակցի կողմից կենսաթոշակն իր բնակարան բերելու միջոցով: Այսինքն` կանխիկ եղանակով, գումարը կենսաթոշակառուին հատկացնելու միջոցով: Ակնհայտ է, որ ստեղծված պայմանները ծառայություն մատուցող կազմակերպություններին ստիպելու են ավելի որակյալ ծառայություններ մատուցել քաղաքացիներին, քանի որ արդեն իսկ գործելու է մրցակցություն, թե որ կազմակերպությունը ինչ որակով ծառայություն կմատուցի: </p><p align="right"><br /><strong>Ամալյա ՀԱՐՈՒԹՅՈՒՆՅԱՆ</strong></p>', 1, 1, 0, 35, '2010-11-04 10:32:19', 64, '', '2010-11-04 12:12:27', 64, 0, '0000-00-00 00:00:00', '2010-11-04 10:32:19', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 6, 0, 124, '', '', 0, 4193, 'robots=\nauthor=');
