INSERT INTO `jos_content` VALUES(182, 'ԱՅՍՕՐ ԿԱՌԱՎԱՐՈՒԹՅՈՒՆՈՒՄ', '2010-11-18-11-35-13', '', '<p><img src="images/stories/407.jpg" border="0" hspace="15" vspace="15" align="left" />Նոյեմբերի 18-ի կառավարության նիստում Կրթության և գիտության նախարար Արմեն Աշոտյանն իր ճեպազրույցի ընթացքում նշեց, որ ՀՀ վարչապետի նոյեմբերի 4-ի հանձնարարությամբ կազմվել է հակակոռուպցիոն ծրագիր, որի քննարկումները պետք է անցկացվեն հրապարակայնորեն:<br />Կոռուպցիոն ռիսկեր կան կրթական համակարգում: Առաջինը` հանրակրթական ոլորտն է, որտեղ դրսևորվում է դրամահավաքների ձևով: Սակայն այդ դրամահավաքությունն արտոնված չէ օրենքով, և նման տեսակի միջոցառումներ անելու իրավունք չունեն:<br />Երկրորդ թիրախը` բուհական համակարգն է. հիմնականում ընթացիկ և ավարտական քննությունների ընթացքում:  <br />Խնդիր է ընդունելության քննություններից հասարակության դժգոհությունը: Եթե դպրոցական և բուհական համակարգում կոռուպցիոն ռիսկերը չեն նվազել, ապա ընդունելության քննությունների դեպքում, առավել ևս վերջին չորս տարիների ընթացքում, համենայնդեպս կոռուպցիոն ռիսկերի տեսակետից, դրանք նվազել են:<br />Ներկայացված ծրագիրը բաղկացած է տասնվեց միջոցառումներից, հիմնական ութ ուղղություններից, որոնցով իրականացվելու է հակակոռուպցիոն պայքարը: </p>', '<p>Առաջինը` իրավական բարեփոխումներն են. ունենք օրենքներ, կարգեր, որոնցում կան երկիմաստ ձևակերպումներ, որոնք և թույլ են տալիս կատարել չարաշահումներ և բացթողումներ: Նախատեսվում է, որ երկու տարվա ընթացքում այս իրավական բարեփոխումները պետք է իրականացվեն: <br />Երկրորդ ուղղությունը` կառավարման որակի բարձրացումն է: Հայտնի է, որ կոռուպցիոն ռիսկերը շատ հաճախ լինում են այնտեղ, որտեղ կառավարման որակը ցածր է: Ամբողջ գործընթացն ընթանալու է ողջ համակարգով. նախարարությունից մինչև բուհեր, դպրոցներ, քոլեջներ: Տարածքային կառավարման մարմիններում կրթության համար պատասխանատուների աշխատանքը բարելավվելու է: Առաջին անգամ Կրթության վարչությունն այս տարի կստուգի մարզերից մեկի համապատասխան վարչությունը, մյուս տարիներին` մնացած մարզերը:<br />Սոցիոլոգիական հարցումները ցույց են տվել, որ հասարակության տեղեկացվածությունը Կրթության և գիտության նախարարության գործունեության վերաբերյալ ամենաբարձրն է: Սակայն պետք է ներմուծել տեղեկատվական նոր տեխնոլոգիաներ:<br />Չորրորդ ուղղությունը` ֆինանսավորման մեխանիզմների հստակեցումն է: Որակից կախված` ֆինանսավորման մեխանիզմների ներմուծում: Դպրոցները, քոլեջները և բուհերը մոտակա երկու տարիների ընթացքում կֆինանսավորվեն նոր ձևերով. կտան կրթական բարձր որակ, կստանան բարձր ֆինանսավորում: Իսկ կրթական այն հաստատությունները, որոնք չեն ունենա բարձր որակ, չեն ստանա բարձր ֆինանսավորում: Սա լավագույն միջոցն է, ըստ միջազգային փորձի, լավ արդյունքների հասնելու համար:<br />Հինգերորդ ուղղությունը` վերահսկողության ուժեղացումն է: Այս հինգ տարիների ընթացքում Կրթության պետական տեսչության վերահսկողությունից դուրս են մնացել մանկապարտեզներն ու բուհերը: Նախատեսվում է ստեղծել Կրթության պետական տեսչության երեք տարածքային կենտրոն. երկուսը` Հայաստանի հյուսիսում և մեկը` հարավում:<br />Վեցերորդ ուղղությունը` քաղաքացիական հասարակության ինստիտուտի ներգրավումն է: Ծնողական խորհուրդները, հասարակական կազմակերպությունները պետք է համագործակցեն հակակոռուպցիոն ռազմավարության ծրագիրն արդյունավետ իրականացնելու համար:<br />Յոթերորդ ուղղությունը` գիտելիքի արժևորումն է հասարակության շրջանում: Տեսահոլովակների, հեռուստահաղորդումների միջոցով պետք է խրախուսել աշակերտներին և ուսանողներին ստանալ լավ գիտելիք:<br />Ութերորդ ուղղությունը` կրթության գործընթացի բարելավումն է, մասնավորապես` ուսանողների վերականգնում, տեղափոխում և ընդունելության քննություններ: Այս գործառույթներում կան որոշակի ռիսկեր, այդ իսկ պատճառով դրանք բարելավման կարիք ունեն:<br /> 2012 թվականի ընդունելության քննությունների համար կկատարվի շատ կտրուկ փոփոխություն. կստեղծվի համապատասխան շտեմարան` իր պատասխաններով: Այսինքն` ընդունելության քննությունը կլինի միայն այդ շտեմարանից: Օգտագործելով միջազգային փորձը` այսուհետ քննություններ կհանձնեն միասնականացված կարգով: </p><p align="right"><br /><strong>Ամալյա ՀԱՐՈՒԹՅՈՒՆՅԱՆ<br /></strong></p>', 1, 1, 0, 35, '2010-11-18 11:21:05', 64, '', '2010-11-18 14:12:43', 64, 0, '0000-00-00 00:00:00', '2010-11-18 11:21:05', 