INSERT INTO `jos_content` VALUES(200, 'ՇՆՈՐՀԱՎՈՐ ՄԱՐՏԻ 8', '-8', '', '<img src="images/stories/rosa.jpg" border="0" hspace="10" vspace="10" align="left" />ՀՀ դատավորների միությունը շնորհավորում է կին դատավորների, ինչպես նաև մայրերի ու քույրերի <strong>Մարտի 8-ը</strong>, մաղթում առողջություն, կանացի անսպառ հմայք, անսահման սեր, անձնական և ընտանեկան երջանկություն:', '', 1, 1, 0, 35, '2011-03-03 06:49:14', 64, '', '2011-03-03 18:55:10', 64, 0, '0000-00-00 00:00:00', '2011-03-03 06:49:14', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 104, '', '', 0, 4064, 'robots=\nauthor=');
