INSERT INTO `jos_content` VALUES(211, 'ՄԻԵԴ ՎՃԻՌՆԵՐԻ ԺՈՂՈՎԱԾՈՒ. ԳԻՐՔ 7', '-7', '', '<p><a href="images/Publications/Am/7ir.pdf"><img src="images/stories/7ir.jpg" border="0" hspace="10" vspace="10" align="left" />ՄԱՐԴՈՒ ԻՐԱՎՈՒՆՔՆԵՐԻ ԵՎՐՈՊԱԿԱՆ ԴԱՏԱՐԱՆ</a> <a href="images/Publications/Am/7ir.pdf">ՎՃԻՌՆԵՐԻ ԺՈՂՈՎԱԾՈՒ</a></p><p><a href="images/Publications/Am/7ir.pdf">ԳԻՐՔ ՅՈԹԵՐՈՐԴ</a></p><p>Մասնագիտական խմբագիր` <strong>Դավիթ ԱՎԵՏԻՍՅԱՆ</strong> - ՀՀ վճռաբեկ դատարանի քրեական պալատի նախագահ, ՀՀ վաստակավոր իրավաբան, իրավագիտության թեկնածու, դոցենտ</p><p>Ընդհանուր խմբագիր` <strong>Վահե ԵՆԳԻԲԱՐՅԱՆ</strong> - ՀՀ դատավորների միության գործադիր տնօրեն, ԵՊՀ դոցենտ, ՀՀ ԳԱԱ ավագ գիտաշխատող, իրավագիտության թեկնածու, դոցենտ</p><p>Վճիռներն անգլերենից թարգմանել են` <strong>Աննա ՎԱՐԴԱՊԵՏՅԱՆԸ, </strong><strong>Լուսինե ԱԲՐԱՀԱՄՅԱՆԸ, </strong><strong>Արմինե ՆԱԶԱՐՅԱՆԸ</strong></p><p>Ժողովածուում ընդգրկվել են ՄԻԵԴ այն վճիռները, որոնք հավակնում են նախադեպային նշանակություն ունենալ ներպետական դատարանների պրակտիկայի համար: Ժողովածուի նպատակն է աջակցել դատավորներին, դատախազներին, փաստաբաններին, ինչպես նաև իրավակիրառ գործունեություն իրականացնող մյուս մարմիններին ու կազմակերպություններին` ծանոթանալու Եվրոպական դատարանի պրակտիկային:</p><p>Ժողովածուն կարող է օգտակար լինել նաև Եվրոպական դատարան դիմելու մտադրություն ունեցող քաղաքացիների, իրավաբանական բուհերի ուսանողների և իրավունքի բնագավառում գիտական հետազոտություններ կատարող անձանց համար:</p>', '', 1, 1, 0, 36, '2011-04-19 05:21:20', 64, '', '2011-04-19 05:37:43', 64, 0, '0000-00-00 00:00:00', '2011-04-19 05:21:20', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=hy-AM\nkeyref=\nreadmore=', 2, 0, 32, '', '', 0, 5465, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES(212, '№ 01/08-Է', '2011-04-27-194723', '', '2008 թվականի ապրիլի 17-ին դռնփակ նիստում, քննության առնելով ՀՀ վերաքննիչ քրեական դատարանի դատավոր Սերժիկ Ավետիսյանի կողմից դատավորի վարքագծի կանոնների խախտում թույլ տալու վերաբերյալ հարցը...', '<style>\r\n.xpost td {\r\n	padding: 5px;\r\n}\r\n\r\n.xpost_bold {\r\n	width: 80%;\r\n}\r\n\r\n.xpost_bold td {\r\n	font-weight: bold;\r\n	vertical-align: bottom;\r\n}\r\n</style>\r\n\r\n<table cellpadding="0" cellspacing="0" width="100%" border="0" class="xpost">\r\n<tr><td align="center">\r\n	<img class="size-thumbnail wp-image-811 aligncenter" title="blazonry" src="http://forum.datavor.am/wp-content/uploads/blazonry-150x150.gif" alt="" width="150" height="150">\r\n	<h2>\r\n		ՀԱՅԱՍՏԱՆԻ ՀԱՆՐԱՊԵՏՈՒԹՅԱՆ<br>\r\n		ԴԱՏԱՐԱՆՆԵՐԻ ՆԱԽԱԳԱՀՆԵՐԻ ԽՈՐՀՈՒՐԴ<br><br>\r\n		ԷԹԻԿԱՅԻ ՀԱՆՁՆԱԺՈՂՈՎ\r\n	</h2>\r\n</td></tr>\r\n<tr><td><br></td></tr>\r\n<tr><td>\r\n	<table cellpadding="0" cellspacing="0" width="100%" border="0">\r\n	<tr>\r\n		<td>№ <u> 01/08-Է </u></td>\r\n		<td align="right">«17» ապրիլի 2008թ.</td>\r\n	</tr>\r\n	</table>\r\n</td></tr>\r\n<tr><td align="center">\r\n	<br><h2>Ո Ր Ո Շ Ու Մ</h2><br>\r\n	<h3>\r\n		ՀՀ ԴԱՏԱՐԱՆՆԵՐԻ ՆԱԽԱԳԱՀՆԵՐԻ ԽՈՐՀՐԴԻ ԷԹԻԿԱՅԻ ՀԱՆՁՆԱԺՈՂՈՎԸ<br>\r\n		/այսուհետ՝ Հանձնաժողով/\r\n	</h3>\r\n</td></tr>\r\n<tr><td><br></td></tr>\r\n<tr><td>\r\n	<table cellpadding="0" cellspacing="0" border="0" align="center" class="xpost_bold">\r\n	<tr>\r\n		<td width="150px">Նախագահությամբ՝</td>\r\n		<td width="50px"></td>\r\n		<td width="50px">Դ.Ավետիսյանի</td>\r\n	</tr>\r\n	<tr>\r\n		<td>Անդամներ՝</td>\r\n		<td></td>\r\n		<td>Տ.Սահակյանի</td>\r\n	</tr>\r\n	<tr>\r\n		<td></td>\r\n		<td></td>\r\n		<td>Էդ.Մուրադյանի</td>\r\n	</tr>\r\n	<tr>\r\n		<td></td>\r\n		<td></td>\r\n		<td>Ս.Մնոյանի</td>\r\n	</tr>\r\n	<tr>\r\n		<td></td>\r\n		<td></td>\r\n		<td>Գ.Հովհաննիսյանի</td>\r\n	</tr>\r\n	<tr>\r\n		<td></td>\r\n		<td></td>\r\n		<td>Հ.Սարգսյանի</td>\r\n	</tr>\r\n	<tr>\r\n		<td></td>\r\n		<td></td>\r\n		<td>Ա.Թումանյանի</td>\r\n	</tr>\r\n	<tr>\r\n		<td>Քարտուղարությամբ՝</td>\r\n		<td></td>\r\n		<td>Ս.Խալաֆյանի</td>\r\n	</tr>\r\n	<tr>\r\n		<td>Մասնակցությամբ`</td>\r\n		<td align="right">դատավոր</td>\r\n		<td>Ս.Ավետիսյանի</td>\r\n	</tr>\r\n	<tr>\r\n		<td>ՀՀ վճռաբեկ դատարանի նախագահ</td>\r\n		<td></td>\r\n		<td>Հ.Մանուկյանի</td>\r\n	</tr>\r\n	<tr>\r\n		<td>ՀՀ վճռաբեկ դատարանի քաղաքացիական պալատի նախագահ</td>\r\n		<td></td>\r\n		<td>Ա.Մկրտումյանի</td>\r\n	</tr>\r\n	<tr>\r\n		<td>ՀՀ դատական դեպարտամենտի ղեկավարի առաջին տեղակալ, ԴՆԽ քարտուղար</td>\r\n		<td></td>\r\n		<td>Մ.Մարտիրոսյանի</td>\r\n	</tr>\r\n	</table>\r\n</td></tr>\r\n<tr><td><br></td></tr>\r\n\r\n<tr><td><p style="text-indent: 20px;">2008 թվականի ապրիլի 17-ին դռնփակ նիստում, քննության առնելով ՀՀ վերաքննիչ քրեական դատարանի դատավոր Սերժիկ Ավետիսյանի կողմից դատավորի վարքագծի կանոնների խախտում թույլ տալու վերաբերյալ հարցը,</p></td></tr>\r\n<tr><td align="center"><br><h3>Պ Ա Ր Զ Ե Ց</h3><br></td></tr>\r\n<tr><td>\r\n	<p style="margin:0px; text-indent: 20px;"><b><u>1. Վարքագծի կանոնների խախտում արձանագրելու հարցը քննարկելու առիթը.</u></b></p>\r\n	<p style="margin:0px; text-indent: 20px;">ՀՀ վերաքննիչ քրեական դատարանի դատավոր Ս.Ավետիսյանի կողմից Դատավորի վարքագծի կանոնագրքի դրույթների խախտում թույլ տալու վերաբերյալ հարցի ուսումնասիրության առիթ է հանդիսացել ՀՀ փաստաբանների պալատի հանրային պաշտպանի գրասենյակի փաստաբաններ՝ Կ.Մանուչարյանի և Ա.Ղազարյանի՝ ՀՀ վճռաբեկ դատարանի նախագահին հասցեագրած դիմումը:</p>\r\n	<p style="margin:0px; text-indent: 20px;">Դիմումի ուսումնասիրության արդյունքում պարզվել է հետևյալը.</p>\r\n	<p style="margin:0px; text-indent: 20px;">Փաստաբաններ Կ.Մանուչարյանը և Ա.Ղազարյանը հայտնել են, որ դատավոր Ս.Ավետիսյանն արդարադատություն իրականացնելիս կատարել է այնպիսի գործողություններ, որոնք խախտում են ՄԻԵԿ 6-րդ հոդվածի 3-րդ կետի պահանջները, կասկած են գցում դատարանի անկողմնակալության վրա, ինչպես նաև վարկաբեկում են դատական իշխանության հեղինակությունը:</p>\r\n	<p style="margin:0px; text-indent: 20px;">Այսպես` մինչ դատական նիստն սկսելը, որը տեղի է ունեցել միայն նախագահող դատավորի, հանրային պաշտպանի և ամբաստանյալների մասնակցությամբ (այսինքն՝ երկու դատավորի և մեղադրողի բացակայությամբ), նախագահող դատավորը դիմել է ամբաստանյալներ Պ.Առաքելյանին և Ս.Գիվարգիզովին և ասել. «Ինչու՞ եք բողոք բերել, առաջին ատյանը նորմալ դատավճիռ է կայացրել, ես ծանոթացել եմ գործին և ոչ մի հեռանկար չեմ տեսնում»: Այնուհետև, դատավոր Ս.Ավետիսյանը, ամբաստանյալներին պարզաբանելով իրենց իրավունքները, առաջարկել է <i>հետ վերցնել բերված վերաքննիչ բողոքները</i>, ինչից վերջիններս հրաժարվել են:</p>\r\n	<p style="margin:0px; text-indent: 20px;">Նիստի սկզբում ամբաստանյալ Ս.Գիվարգիզովը, հիմք ընդունելով ՀՀ քրեական դատավարության օրենսգրքի 65-րդ հոդվածի 2-րդ մասի 16-րդ կետի պահանջը, գրավոր միջնորդել է անվճար տրամադրել քննվող քրեական գործի նյութերի պատճենները, ինչը մերժվել է դատավորի կողմից: Այս դեպքում նախագահող դատավորը պարզաբանել է, որ օրենքով անվճար կարող են տրամադրվել միայն ՀՀ քրեական դատավարության օրենսգրքի 65-րդ հոդվածի 2-րդ մասի 19-րդ կետում նշված դատավարական փաստաթղթերը:</p>\r\n	<p style="margin:0px; text-indent: 20px;">2008թ. հունվարի 7-ին հանրային պաշտպան Կ.Մանուչարյանը գրավոր միջնորդություն է ներկայացրել դատավոր Ս.Ավետիսյանին՝ խնդրելով բավարարել ամբաստանյալ Ս.Գիվարգիզովի 28.12.07թ. միջնորդությունը, ինչը կրկին մերժվել է դատավոր Ս.Ավետիսյանի կողմից:</p>\r\n	<p style="margin:0px; text-indent: 20px;">Հետաձգելով երկու դատավորների և մեղադրողի բացակայությամբ անցկացվող 28.12.2007թ.-ի դատական նիստը՝ նախագահող դատավոր Ս.Ավետիսյանը հայտնել է հաջորդ նիստի օրը և ժամը՝ 16.01.2008թ. ժամը 16:30: Նկատի ունենալով երկրորդ ամբաստանյալի՝ Պեղատոս Առաքելյանի՝ պաշտպան ունենալու ցանկությունը, նախագահող դատավորը նաև որոշում է կայացրել ՀՀ ՓՊ ՀՊԳ հանրային պաշտպանի տրամադրման վերաբերյալ: Նույն քրեական գործով ամբաստանյալ Պ.Առաքելյանի պաշտպան նշանակված՝ ՓՊ ՀՊԳ փաստաբան Ա.Ղազարյանը և փաստաբան Կ.Մանուչարյանը 16.01.2008թ. ժամը 16:15-ին ներկայացել են ՀՀ վերաքննիչ քրեական դատարան` նշված քրեական գործի քննությանը մասնակցելու նպատակով, սակայն, ինչպես իրենց հայտնել է դատավոր Ս.Ավետիսյանի օգնականը, <i>նիստն արդեն կայացել է ժամը 11:30-ին</i>:</p>\r\n	<p style="margin:0px; text-indent: 20px;">Հետաձգելով 28.12.2007թ. նիստը և նշանակելով հաջորդ դատական նիստը 16.01.08թ. ժամը 16:30-ին (ինչի վերաբերյալ արձանագրված է 28.12.2007թ. նիստի ձայնագրառումով՝ ժամը 12:21 րոպեին)` նախագահողը 16.01.08թ. նիստը սկսել է ժամը 11:30-ին, այսինքն՝ նշանակվածից 5 (հինգ) ժամ առաջ, ինչի պատճառով հանրային պաշտպաններ Կ.Մանուչարյանը և Ա.Ղազարյանը զրկված են եղել դատաքննությանը մասնակցելու հնարավորությունից:</p>\r\n	<p style="margin:0px; text-indent: 20px;">Նույն օրը՝ 16.01.2008թ., դատական նիստի ընթացքում դատավոր Ս.Ավետիսյանը հայտնել է ամբաստանյալներին իրականությանը չհամապատասխանող այն տեղեկությունը, իբր պաշտպանները հրաժարվել են պաշտպանությունից, այնուհետև ընդունել է ամբաստանյալ Պ.Առաքելյանի՝ պաշտպանից հրաժարվելու հայտարարությունը, ինչը հակասում է ՀՀ քրեական դատավարության օրենսգրքի 72-րդ հոդվածի 2-րդ մասին, որի համաձայն` «Պաշտպանից հրաժարվելը քրեական վարույթն իրականացնող մարմինն ընդունում է միայն այն դեպքում, եթե...մեղադրյալի կողմից այդ մասին հայտարարվել է .... այն պաշտպանի ներկայությամբ, որը կարող էր նշանակվել կամ նշանակված է պաշտպան»:</p>\r\n	<p style="margin:0px; text-indent: 20px;">Հանրային պաշտպան Կ.Մանուչարյանի և ամբաստանյալ Ս.Գիվարգիզովի կողմից բացարկ է հայտնվել (ինքնաբացարկի միջնորդություն) նիստը նախագահող դատավոր Ս.Ավետիսյանին, որը դատարանի որոշմամբ մերժվել է:</p>\r\n	<p style="margin:0px; text-indent: 20px;">08.02.2008թ. հանրային պաշտպաններ Կ.Մանուչարյանի և Ա.Ղազարյանի կողմից բացարկ է հայտնվել ՀՀ քրեական վերաքննիչ դատարանի ամբողջ կազմին, որը նույնպես չի ընդունվել:</p>\r\n	<p style="margin:0px; text-indent: 20px;">Ըստ բողոքաբերների, նախագահողի գործողությունները խախտել են ՄԻԵԿ 6-րդ հոդվածի 3-րդ կետի բ) ենթակետի պահանջը, որն ամրագրում է քրեական հանցագործություն կատարելու մեջ մեղադրվող յուրաքանչյուր անձի իրավունքը՝ «բավարար ժամանակ ու հնարավորություններ ունենալու իր պաշտպանությունը նախապատրաստելու համար»:</p>\r\n	<p style="margin:0px; text-indent: 20px;">ՀՀ վերաքննիչ քրեական դատարանի անօրինական գործողությունները ոչ միայն չեն վկայում դատարանի անկողմնակալության մասին, այլ նաև վարկաբեկում են դատական իշխանության հեղինակությունը, զրկում են պաշտպանության կողմին ամբաստանյալի օրինական շահերը որակյալ ներկայացնելու հնարավորությունից: Վերը նշված փաստերը հավաստվում են դատական նիստերի ձայնագրառումներով:</p>\r\n	<p style="margin:0px; text-indent: 20px;">Պաշտպանները խնդրել են ձեռնարկել համապատասխան միջոցներ:</p><br>\r\n\r\n	<p style="margin:0px; text-indent: 20px;"><b><u>2. Դատավորի առարկություններում բերված փաստարկները.</u></b></p>\r\n	<p style="margin:0px; text-indent: 20px;">ՀՀ վերաքննիչ քրեական դատարանի դատավոր Ս.Ավետիսյանը ժխտել է դիմումի մեջ նշված փաստերի առկայությունը` վիճարկելով դրանց որակումը որպես վարքագծի կանոնների խախտում:</p>\r\n	<p style="margin:0px; text-indent: 20px;">Մասնավորապես, դատավոր Ս.Ավետիսյանը նշել է, որ իր վարույթում գտնվող քրեական գործով ըստ մեղադրանքի՝ Սերգեյ Ժորայի Գիվարգիզովի ՀՀ քրեական օրենսգրքի 175-րդ հոդվածի 3-րդ մասի 4-րդ կետով և Պեղատոս Եղիշի Առաքելյանի ՀՀ քրեական օրենսգրքի 175-րդ հոդվածի 2-րդ մասի 1-ին և 4-րդ կետերով, հանրային պաշտպաններ Կ.Մանուչարյանի և Ա.Ղազարյանի կողմից ՀՀ վճռաբեկ դատարանի նախագահին հասցեագրած դիմումն անհիմն է և իրականությանը չի համապատասխանում հետևյալ պատճառաբանությամբ.</p>\r\n	<p style="margin:0px; text-indent: 20px;">Ամբաստանյալների վերաքննիչ բողոքների հիման վրա դատարանի 24.12.2007թ. որոշմամբ հիշյալ քրեական գործը նշանակվել է դատարանի դռնբաց դատական նիստում քննության 28.12.2007թ.` ժամը 11:30-ին` կանչելով ամբաստանյալներին, առաջին ատյանի դատարանում մասնակցած հանրային պաշտպան Լուիզա Հարությունյանին և մեղադրողին:</p>\r\n	<p style="margin:0px; text-indent: 20px;">28.12.2007թ. ժամը 12-ին Ս.Ավետիսյանը` որպես նախագահող դատավոր, դատական կազմը չհամալրված լինելու պատճառով նիստերի քարտուղար Մարիա Հակոբյանի հետ իջել է թիվ 3-րդ դատական նիստերի դահլիճ, որտեղ մինչ դատական նիստն սկսելը կատարել է հետևյալ գործողությունները.</p>\r\n	<p style="margin:0px; text-indent: 20px;">1. պարզել է, որ դատական նիստին ներկայացել են ամբաստանյալները, ներկայացել են հանրային պաշտպան Կարեն Մանուչարյանը, չեն ներկայացել դատախազը և ամբաստանյալ Պ.Առաքելյանի շահերի հանրային պաշտպան Լ.Հարությունյանը:</p>\r\n	<p style="margin:0px; text-indent: 20px;">2. Դատավարության մասնակիցներին հայտնել է, որ դատական կազմում ընդգրկված դատավոր Ռ.Ափինյանը տեղափոխվել է Կենտրոն և Նորք-Մարաշ համայնքների առաջին ատյանի դատարան` նշելով, որ նոր կազմ համալրել կադրային փոփոխությունների պատճառով հնրարավոր չի եղել:</p>\r\n	<p style="margin:0px; text-indent: 20px;">3. Որպես նախագահող դատավոր, ամբաստանյալներին բացատրել է ՀՀ քրեական դատավարության օրենսգրքի 65-րդ հոդվածի 2-րդ մասի 21-րդ կետով և 383-րդ հոդվածով ամրագրված իրավունքը՝ մինչև վերաքննիչ դատարանում դատական նիստն սկսելը բողոքները հետ վերցնելու վերաբերյալ: Ամբաստանյալները հայտնել են, որ այդ իրավունքից չեն օգտվում և ցանկանում են, որպեսզի քրեական գործը վերանայվի:</p>\r\n	<p style="margin:0px; text-indent: 20px;">4. Պաշտպան Կ.Մանուչարյանը և ամբաստանյալները չեն առարկել, որպեսզի դատավորը դատական նիստը բացի և նշված հիմքերով (դատավորի և դատախազի բացակայությունը) հետաձգի:</p>\r\n	<p style="margin:0px; text-indent: 20px;">Հիշյալ գործողությունների վերաբերյալ դատական նիստերի քարտուղարը նույն օրը կազմել է համապատասխան տեղեկանք (կցվում է):</p>\r\n	<p style="margin:0px; text-indent: 20px;">Այս գործողությունները ավարտելուց հետո դատավոր Ս.Ավետիսյանը կատարել է իր վրա դրված պարտականությունները (ՀՀ քր.դատ. օր-ի 39, 41-43, 390 հոդվածներով), դատական նիստը հայտարարել է բացված և չկրկնվելու պատճառով չի նշել, թե ինչու է միանձնյա վարում նիստը: Պաշտպան Կ.Մանուչարյանը խնդրել է դատական նիստը ընդմիջել նաև այն նպատակով, որպեսզի իր պաշտպանյալին ՀՀ քրեական դատավարության օրենսգրքի 65-րդ հոդվածի 2-րդ մասի 16-րդ կետի հիման վրա անվճար տրվի քրեական գործի նյութերը: Պաշտպանին բացատրելով նույն հոդվածի 19-րդ կետի դրույթները` Ս.Ավետիսյանը հայտարարել է, որ ամբաստանյալներին անվճար կտրվեն համապատասխան նյութերը և կներկայացվեն գործի ամբողջ նյութերը ծանոթանալու համար:</p>\r\n	<p style="margin:0px; text-indent: 20px;">Նիստը հիշյալ հիմքերով հետաձգվել է մինչև 16.01.2008թ.-ի ժամը 11:30-ը:</p>\r\n	<p style="margin:0px; text-indent: 20px;">Այսպիսով, դատավոր Ս.Ավետիսյանը նշել է, որ իր կողմից 28.12.07թ. ինչպես մինչև դատական նիստ բացելը, այնպես էլ բացելուց հետո <i>որևէ խախտում թույլ չի տրվել</i>:</p>\r\n	<p style="margin:0px; text-indent: 20px;">Ս.Ավետիսյանը նշել է, որ բողոքը հետ վերցնելու գործառույթը թույլատրվում է վերաքննիչ դատարանում իրականացնել և՛ նախագահող դատավորի կողմից, և՛ դատական կազմի մասնակցությամբ, և՛ դատավորների բացակայության պայմաններում:</p>\r\n	<p style="margin:0px; text-indent: 20px;">Նախագահող դատավորը նշել է նաև, որ ինչ վերաբերում է այն հանգամանքին, որ իբր նա ասել է, որ վերաքննիչ բողոքները բավարարելու հիմքեր չկան, <i>անհիմն է</i>: Պաշտպան Կ.Մանուչարյանը դատավորի որևէ գործողության դեմ չի առարկել և միայն հետագայում, դժգոհ մնալով այն բանից, որ դատարանը ամբաստանյալներին անվճար ամբողջ գործի նյութերը չի տրամադրել, 16.01.2008թ. սկսած, հոգեբանական ճնշումներ է գործադրել և սպառնալիքներ տվել, որ ամեն տեղ կբողոքարկի իր գործողությունները:</p>\r\n	<p style="margin:0px; text-indent: 20px;">Վերոհիշյալն ապացուցվել է նաև 16.01.2008թ. դատական նիստի համակարգչային ձայնագրման կրիչի տվյալներով, որտեղ Ս.Ավետիսյանը (դատավորներ՝ Մ.Սիմոնյան, Ա.Սիմոնյան, Ա.Խաչատրյան) դատավարության մասնակիցներին կրկին հիշեցրել է, որ 28.12.07թ. դատական նիստը հետաձգվել է դատավոր Ռ.Ափինյանի տեղափոխման պատճառով` հավելելով, որ առկա է նոր դատական կազմ, որի պատճառով էլ դատական նիստն ըստ էության բացվել է 16.01.2008թ.: Այդ կապակցությամբ որևէ առարկություն չի եղել:</p>\r\n	<p style="margin:0px; text-indent: 20px;">16.01.2008թ. դատական նիստը նշանակված է եղել ժամը 11:30-ին, այլ ոչ թե օրվա վերջում՝ ժամը 16:30-ը, ինչը նշում են փաստաբանները:</p>\r\n	<p style="margin:0px; text-indent: 20px;">Այսպես, հանրային պաշտպան նշանակելու մասին վերաքննիչ դատարանի 28.12.2007թ. որոշումից երևում է, որ նիստը նշանակվել է 16.01.2008թ.-ին` ժամը 11:30-ին: Այդ որոշման պատճենը Կ.Մանուչարյանը ստացել է և փոխանցել հանրային պաշտպանի գրասենյակ: Գրասենյակի ղեկավար Մ.Փիլիպոսյանն այդ որոշման հիման վրա որոշում է կայացրել Ա.Ղազարյանին որպես հանրային պաշտպան ներգրավելու մասին` նշելով, որ «նիստը տեղի կունենա 16.01.2008թ. ժամը 11:30-ին»:</p>\r\n	<p style="margin:0px; text-indent: 20px;">Այդ օրը ամբաստանյալ Պ.Առաքելյանը դատախազի մասնակցությամբ հայտնել է, որ չի ցանկանում ոչ հանրային պաշտպան, ոչ էլ պայմանագրային հիմքով պաշտպան ունենալ: Դատարանն ընդունել է նրա հայտարարությունը: 16.01.2008թ. պաշտպան Կ.Մանուչարյանը եղել է անհասանելի և դատական նիստին չի ներկայացել, ինչը Ս.Գիվարգիզովի մոտ դժգոհություն է առաջացրել և ցանկացել է հրաժարվել Կ.Մանուչարյանից: Սակայն նախագահող դատավոր Ս.Ավետիսյանի առաջարկությամբ դատարանը Գիվարգիզովի՝ պաշտպանից հրաժարվելու միջնորդությունը չի ընդունել, որի պատճառով նիստը հետաձգվել է:</p>\r\n	<p style="margin:0px; text-indent: 20px;">Ս.Ավետիսյանը նույն օրը գրավոր տեղեկացրել է հանրային պաշտպանի գրասենյակի պետ Մ.Փիլիպոսյանին (16.01.2008թ.թիվ 29-658/08 գրություն):</p>\r\n	<p style="margin:0px; text-indent: 20px;">Հետագայում ամբաստանյալ Պ.Առաքելյանը փոխել է դիրքորոշումը և խնդրել ապահովել հանրային պաշտպանով:</p>\r\n	<p style="margin:0px; text-indent: 20px;">Դատավոր Ս.Ավետիսյանը նշել է, որ իր նախագահությամբ դատարանի կազմը մերժել է ամբաստանյալներին քրեական գործի ամբողջ նյութերն անվճար տրամադրելու բազմաթիվ միջնորդությունները` կայացնելով պատճառաբանված և հիմնավորված որոշումներ: Այդ պատճառով պաշտպանները դատավորին, այնուհետև` դատարանի ամբողջ կազմին բացարկ են հայտնել: Բացարկները անհիմն լինելու պատճառաբանությամբ մերժվել են:</p>\r\n	<p style="margin:0px; text-indent: 20px;">Դատավորը նշել է, որ դիմումում թվարկված բոլոր փաստարկներն անհիմն և իրականությունից զուրկ են: Ամբաստանյալները ՀՀ քրեական դատավարության օրենսգրքի 65-րդ հոդվածի 2-րդ մասի 19-րդ կետի հիմքով անվճար ստացել են 37 թերթից բաղկացած փաստաթղթեր, ինչը հաստատել են գրավոր ստացականներով: Քրեական գործի ամբողջ նյութերը ծանոթանալու համար տրվել են և'' պաշտպաններին, և'' ամբաստանյալներին (կազմվել են համապատասխան տեղեկանքներ):</p>\r\n	<p style="margin:0px; text-indent: 20px;">Դատավոր Ս.Ավետիսյանը նշել է, որ իր և կազմի դատավորների կողմից սույն գործով արդարադատություն իրականացնելիս նյութական կամ դատավարական օրենքի պահանջների, ինչպես նաև դատավորի վարքագծի կանոնների (էթիկայի) խախտումներ թույլ չեն տրվել:</p><br>\r\n\r\n	<p style="margin:0px; text-indent: 20px;"><b><u>3. Դատավորի վարքագծի կանոնների խախտում կատարելու վերաբերյալ գործի քննության համար էական նշանակություն ունեցող փաստերը.</u></b></p>\r\n	<p style="margin:0px; text-indent: 20px;">Հանձնաժողովը հիմք է ընդունում արձանագրված հետևյալ փաստերը.</p>\r\n	<p style="margin:0px; text-indent: 20px;">1. ՀՀ վերաքննիչ դատարանի դատավոր Ս.Ավետիսյանի վարույթում գտնվող քրեական գործով ըստ մեղադրանքի՝ Սերգեյ Ժորայի Գիվարգիզովի ՀՀ քրեական օրենսգրքի 175-րդ հոդվածի 3-րդ մասի 4-րդ կետով և Պեղատոս Եղիշի Առաքելյանի ՀՀ քրեական օրենսգրքի 175-րդ հոդվածի 2-րդ մասի 1-ին և 4-րդ կետերով՝ վերաքննիչ բողոքների հիման վրա 24.12.2007թ. որոշմամբ հիշյալ քրեական գործը նշանակվել է դատարանի դռնբաց դատական նիստում քննության 28.12.2007թ.-ին` ժամը 11:30-ին` կանչելով ամբաստանյալներին, առաջին ատյանի դատարանում մասնակցած հանրային պաշտպան Լուիզա Հարությունյանին և մեղադրողին:</p>\r\n	<p style="margin:0px; text-indent: 20px;">2. 28.12.2007թ. ժամը 12-ին նախագահող դատավորը նիստը հետաձգել է՝ դատական կազմը համալրված չլինելու պատճառով:</p>\r\n	<p style="margin:0px; text-indent: 20px;">3. 16.01.2008թ. դատական նիստի համակարգչային ձայնագրման կրիչի տվյալներով Ս.Ավետիսյանը (դատավորներ՝ Մ.Սիմոնյան, Ա.Սիմոնյան, Ա.Խաչատրյան) դատավարության մասնակիցներին կրկին հիշեցնում է, որ 28.12.07թ. դատական նիստը հետաձգվել է դատավոր Ռ.Ափինյանի տեղափոխման պատճառով` հավելելով, որ առկա է նոր դատական կազմ, որի պատճառով էլ դատական նիստը ըստ էության բացվել է 16.01.2008թ.: Այդ կապակցությամբ որևէ առարկություն չի եղել:</p>\r\n	<p style="margin:0px; text-indent: 20px;">4. 16.01.2008թ. դատական նիստը նշանակված է եղել ժամը 11:30-ի` այլ ոչ թե ժամը 16:30-ին, ինչպես նշում են պաշտպանները: Հանրային պաշտպան նշանակելու մասին վերաքննիչ դատարանի 28.12.2007թ. որոշումից երևում է, որ նիստը նշանակվել է 16.01.2008թ. ժամը 11:30-ին, որի պատճենը Կ.Մանուչարյանը ստացել և փոխանցել է հանրային պաշտպանի գրասենյակ: Գրասենյակի ղեկավար Մ.Փիլիպոսյանն այդ որոշման հիման վրա որոշում է կայացրել Ա.Ղազարյանին որպես հանրային պաշտպան ներգրավելու մասին՝ նշելով, որ «նիստը տեղի կունենա 16.01.2008թ. ժամը 11:30-ին»:</p>\r\n	<p style="margin:0px; text-indent: 20px;">5. Պ.Առաքելյանը դատախազի մասնակցությամբ հայտնել է, որ չի ցանկանում ոչ հանրային պաշտպան, ոչ էլ պայմանագրային հիմքով պաշտպան ունենալ: Դատարանն ընդունել է նրա հայտարարությունը: 16.01.2008թ. պաշտպան Կ.Մանուչարյանը եղել է անհասանելի և դատական նիստին չի ներկայացել, ինչը Ս.Գիվարգիզովի մոտ դժգոհություն է առաջացրել և ցանկացել է հրաժարվել Կ.Մանուչարյանից: Սակայն նախագահող դատավոր Ս.Ավետիսյանի առաջարկությամբ դատարանը Գիվարգիզովի՝ պաշտպանից հրաժարվելու միջնորդությունը չի ընդունել, որի պատճառով նիստը հետաձգվել է:</p>\r\n	<p style="margin:0px; text-indent: 20px;">6. Ս.Ավետիսյանը նույն օրը գրավոր տեղեկացրել է հանրային պաշտպանի գրասենյակի պետ Մ.Փիլիպոսյանին (16.01.2008թ.թիվ 29-658/08 գրություն): Հետագայում ամբաստանյալ Պ.Առաքելյանը փոխել է դիրքորոշումը և խնդրել ապահովել հանրային պաշտպանով:</p>\r\n	<p style="margin:0px; text-indent: 20px;">7. Փաստաբաններ Կ.Մանուչարյանը և Ա.Ղազարյանը դիմում են ներկայացրել, որ դատավոր Ս.Ավետիսյանը արդարադատություն իրականացնելիս թույլ է տրվել այնպիսի գործողություններ, որոնք խախտում են ՄԻԵԿ 6-րդ հոդվածի 3-րդ կետի պահանջները, կասկած են գցում դատարանի անկողմնակալության վրա, ինչպես նաև վարկաբեկում են դատական իշխանության հեղինակությունը:</p><br>\r\n\r\n	<p style="margin:0px; text-indent: 20px;"><b><u>4. Հանձնաժողովի պատճառաբանությունները և եզրահանգումները.</u></b></p>\r\n	<p style="margin:0px; text-indent: 20px;">Քննարկելով ՀՀ վերաքննիչ դատարանի դատավոր Սերժիկ Ավետիսյանի կողմից Դատավորի վարքագծի կանոնագրքի խախտում թույլ տալու վերաբերյալ հայցը, ուսումնասիրելով ՀՀ փաստաբանների պալատի հանրային պաշտպանի գրասենյակի փաստաբաններ Կ.Մանուչարյանի և Ա.Ղազարյանի դիմումը, լսելով դատավորի բացատրությունները, ուսումնասիրելով գործի նյութերը և հետազոտելով ապացույցները՝ Հանձնաժողովը գտնում է, որ դիմումում թվարկված բոլոր փաստարկներն անհիմն են հետևյալ պատճառաբանությամբ.</p>\r\n	<p style="margin:0px; text-indent: 20px;">ՀՀ վերաքննիչ դատարանի դատավոր Ս.Ավետիսյանի վարույթում գտնվող քրեական գործով ըստ մեղադրանքի՝ Սերգեյ Ժորայի Գիվարգիզովի ՀՀ քրեական օրենսգրքի 175-րդ հոդվածի 3-րդ մասի 4-րդ կետով և Պեղատոս Եղիշի Առաքելյանի ՀՀ քրեական օրենսգրքի 175-րդ հոդվածի 2-րդ մասի 1-ին և 4-րդ կետերով վերաքննիչ բողոքների հիման վրա 24.12.2007թ. որոշմամբ հիշյալ քրեական գործը նշանակվել է դատարանի դռնբաց դատական նիստում քննության 28.12.2007թ.-ին` ժամը 11:30-ին, և նույն ժամին էլ սկսվել է:</p>\r\n	<p style="margin:0px; text-indent: 20px;">28.12.2007թ. ժամը 12-ին նախագահող դատավորը դատական կազմը չհամալրված լինելու պատճառով (դատավորի և դատախազի բացակայությունը) և պաշտպան Կ.Մանուչարյանի` դատական նիստն ընդմիջելու խնդրանքով դատական նիստը հետաձգել է:</p>\r\n	<p style="margin:0px; text-indent: 20px;">Դատավորը պաշտպանին բացատրել է ՀՀ քրեական դատավարության օրենսգրքի 65-րդ հոդվածի 2-րդ մասի 19-րդ կետի դրույթները և հայտարարել, որ ամբաստանյալներին անվճար կտրվեն համապատասխան նյութերը և կներկայացվեն գործի ամբողջ նյութերը ծանոթանալու համար, ինչը և արվել է դատավոր Ս.Ավետիսյանի կողմից:</p>\r\n	<p style="margin:0px; text-indent: 20px;">16.01.2008թ. դատական նիստը նշանակված է եղել ժամը 11:30-ին, այլ ոչ թե ժամը 16:30-ին, ինչպես նշում են պաշտպանները: Դատական նիստն սկսվել է նշանակված ժամին, որի մասին պատշաճ ծանուցվել են կողմերը: Դրա ապացույցն է հանրային պաշտպան նշանակելու մասին ՀՀ վերաքննիչ դատարանի 28.12.2007թ. որոշումը, որի պատճենը Կ.Մանուչարյանն ստացել և փոխանցել է հանրային պաշտպանի գրասենյակ:</p>\r\n	<p style="margin:0px; text-indent: 20px;">Պ.Առաքելյանը դատախազի մասնակցությամբ հայտնել է, որ չի ցանկանում ոչ հանրային պաշտպան, ոչ էլ պայմանագրային հիմքով պաշտպան ունենալ: Դատարանն ընդունել է նրա հայտարարությունը: Նույն օրը գրավոր տեղեկացրել է հանրային պաշտպանի գրասենյակի պետ Մ.Փիլիպոսյանին (16.01.2008թ.թիվ 29-658/08 գրություն): Հետագայում ամբաստանյալ Պ.Առաքելյանը փոխել է դիրքորոշումը և խնդրել ապահովել հանրային պաշտպանով:</p>\r\n	<p style="margin:0px; text-indent: 20px;">Հաշվի առնելով վերոգրյալը՝ Հանձնաժողովը գտնում է, որ դատավոր Ս.Ավետիսյանի կողմից դատավորի վարքագծի կանոնների խախտումներ թույլ չեն տրվել և դիմումում թվարկված բոլոր փաստարկները <i>անհիմն են</i>, իրականությունից զուրկ, քանի որ դիմումատուները չեն ապացուցել իրենց կողմից ներկայացված փաստերը:</p>\r\n	<p style="margin:0px; text-indent: 20px;">Ելնելով վերոգրյալից և ղեկավարվելով ՀՀ դատարանների նախագահների խորհրդի էթիկայի հանձնաժողովի կանոնակարգի 3.10-րդ կետով` Հանձնաժողովը</p>\r\n</td></tr>\r\n<tr><td align="center"><br><h3>Ո Ր Ո Շ Ե Ց</h3><br></td></tr>\r\n<tr><td><p style="text-indent: 20px;">ՀՀ վերաքննիչ քրեական դատարանի դատավոր <i>Սերժիկ Ավետիսյանի</i> կողմից Դատավորի վարքագծի կանոնագրքի դրույթների խախտումներ թույլ չեն տրվել:</p></td></tr>\r\n<tr><td><br></td></tr>\r\n<tr><td>\r\n	<table cellpadding="0" cellspacing="0" align="center" class="xpost_bold">\r\n	<tr>\r\n		<td width="150px">ՀՀ դատարանների նախագահների խորհրդի էթիկայի հանձնաժողովի նախագահ՝</td>\r\n		<td width="50px"></td>\r\n		<td width="50px">Դ.ԱՎԵՏԻՍՅԱՆ</td>\r\n	</tr>\r\n	<tr>\r\n		<td>Հանձնաժողովի անդամներ՝</td>\r\n		<td></td>\r\n		<td>Ս.ՄՆՈՅԱՆ</td>\r\n	</tr>\r\n	<tr>\r\n		<td></td>\r\n		<td></td>\r\n		<td>Տ.ՍԱՀԱԿՅԱՆ</td>\r\n	</tr>\r\n	<tr>\r\n		<td></td>\r\n		<td></td>\r\n		<td>Է.ՄՈՒՐԱԴՅԱՆ</td>\r\n	</tr>\r\n	<tr>\r\n		<td></td>\r\n		<td></td>\r\n		<td>Ա.ԹՈՒՄԱՆՅԱՆ</td>\r\n	</tr>\r\n	<tr>\r\n		<td></td>\r\n		<td></td>\r\n		<td>Հ.ՍԱՐԳՍՅԱՆ</td>\r\n	</tr>\r\n	<tr>\r\n		<td></td>\r\n		<td></td>\r\n		<td>Գ.ՀՈՎՀԱՆՆԻՍՅԱՆ</td>\r\n	</tr>\r\n	</table>\r\n</td></tr>\r\n</table>', 1, 6, 0, 41, '2011-04-27 19:47:23', 62, '', '2011-04-27 19:58:28', 62, 64, '2014-04-06 18:02:17', '2011-04-27 19:47:23', '0000-00-00 00:00:00', '', '', 'show_title=\r\nlink_titles=\r\nshow_intro=\r\nshow_section=\r\nlink_section=\r\nshow_category=\r\nlink_category=\r\nshow_vote=\r\nshow_author=0\