INSERT INTO `jos_content` VALUES(235, 'ՔՆՆԱՐԿՈՒՄ ՍՏՐԱՍԲՈՒՐԳՈՒՄ', '2011-05-26-07-49-16', '', '<p align="right"><strong><em><img src="images/stories/st1.jpg" border="0" hspace="10" vspace="10" align="left" />2011թ. մայիսի 18-ին Ստրասբուրգում տեղի ունեցավ քննարկում` "Արևելյան երկրներում դատաիրավական բարեփոխումների բարձրացում" թեմայով: ՀՀ դատական համակարգի պատվիրակության կազմում ընդգրկված էին Հրաչիկ Սարգսյանը (ՀՀ դատավորների միության նախագահ, Արմավիրի մարզի ընդհանուր իրավասության դատարանի նախագահ), Երվանդ Խունդկարյանը /ՀՀ վճռաբեկ դատարանի քաղաքացիական և վարչական պալատի նախագահ/, Վահե Ենգիբարյանը /ՀՀ դատավորների միության գործադիր տնօրեն և Սերգեյ Մարաբյանը /ՀՀ վճռաբեկ դատարանի կազմակերպաիրավական վարչության պետ/: </em></strong></p><strong><em><p>   Քննարկվեցին տարաբնույթ հարցեր, որոնք առանցքային նշանակություն ունեն ՀՀ դատական համակարգի համար: Հնչեցին հարցեր, պատասխաններ: Պատվիրակության մասնակիցները բարձրացրեցին ՀՀ դատական համակարգին վերաբերող հարցեր: Կազմակերպիչների կողմից ձևավորվեցին աշխատանքային խմբեր երեք ուղղություններով` դատական համակարգի անկախություն, մասնագիտացված դատական համակարգեր և արդյունավետ դատական համակարգեր: <br />   ՀՀ պատվիրակության անդամները ևս ներգրավվեցին հետագա աշխատանքներին: Աշխատանքային խմբի հաջորդ հանդիպումը տեղի կունենա սույն թվականի հունիսի 9-10-ը:</p></em></strong>', '', -2, 1, 0, 35, '2011-05-26 07:33:03', 64, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2011-05-26 07:33:03', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=hy-AM\nkeyref=\nreadmore=', 1, 0, 0, '', '', 0, 0, 'robots=\nauthor=');
