INSERT INTO `jos_content` VALUES(256, 'ՊԱՇՏՈՆԱԿԱՆ ԱՅՑ ՉԻՆԱՍՏԱՆԻ ԺՈՂՈՎՐԴԱԿԱՆ ՀԱՆՐԱՊԵՏՈՒԹՅՈՒՆ', '2011-11-09-07-11-31', '', '<p align="justify"><img src="images/stories/chinastan.jpg" border="0" hspace="7" vspace="7" align="left" />Չինաստանի Ժողովրդական Հանրապետության Գերագույն ժողովրդական դատարանի նախագահ Վան Շենցզյունի հրավերով 2011 թվականի հոկտեմբերի 24-ից 29-ը ՀՀ դատական իշխանության պատվիրակությունը պաշտոնական այց կատարեց Չինաստանի Ժողովրդական Հանրապետություն: Պատվիրակությունը ղեկավարում էր ՀՀ վճռաբեկ դատարանի նախագահ Արման Մկրտումյանը: Պատվիրակության կազմում էին ՀՀ վճռաբեկ դատարանի քրեական պալատի նախագահ Դավիթ Ավետիսյանը, ՀՀ վերաքննիչ քրեական դատարանի նախագահ Տիգրան Սահակյանը, ՀՀ վարչական դատարանի նախագահ Կարեն Մաթևոսյանը, Երևան քաղաքի Կենտրոն և Նորք-Մարաշ վարչական շրջանների ընդհանուր իրավասության դատարանի նախագահ Ռուբեն Վարդազարյանը, Արմավիրի մարզի ընդհանուր իրավասության դատարանի նախագահ, ՀՀ դատավորների միության նախագահ Հրաչիկ Սարգսյանը և Սյունիքի մարզի ընդհանուր իրավասության դատարանի նախագահ Սամվել Գրիգորյանը:</p>', '<p align="justify">Պաշտոնական այցի շրջանակներում հանդիպումներ տեղի ունեցան Չինաստանի Ժողովրդական Հանրապետության Գերագույն ժողովրդական դատարանի նախագահ Վան Շենցզյունի, նրա տեղակալների և դատարանի պալատների նախագահների հետ: Հանդիպման ընթացքում Չինաստանի Ժողովրդական Հանրապետության Գերագույն ժողովրդական դատարանի նախագահ Վան Շենցզյունը ողջունեց պատվիրակությանը` նշելով, որ երկու երկրների դատական համակարգերի միջև լուրջ համագործակցության հիմքեր են դրվում, և ներկայացրեց Չինաստանի դատական համակարգը և դատարանների գործունեությունը:</p><p align="justify">ՀՀ վճռաբեկ դատարանի նախագահ Արման Մկրտումյանը ներկայացրեց Հայաստանի Հանրապետությունում իրականացված և իրականացվող դատաիրավական բարեփոխումները` նշելով, որ այդ գործընթացներն ունեն սկիզբ, բայց չունեն վերջ, քանի որ արդարադատության բնագավառում իրականացվող բարեփոխումներն երբեք չեն կարող ավարտվել: </p><p align="justify">ՀՀ վճռաբեկ դատարանի նախագահ Ա.Մկրտումյանը շնորհակալություն հայտնեց Վան Շենցզյունին Չինաստանի Ժողովրդական Հանրապետության և Հայաստանի Հանրապետության դատական համակարգերի համագործակցության և փորձի փոխանակման հարցում դրսևորած նախաձեռնության համար: Այդ կապակցությամբ Ա.Մկրտումյանը Չինաստանի Ժողովրդական Հանրապետության Գերագույն ժողովրդական դատարանի նախագահ Վան Շենցզյունին հանձնեց ՀՀ դատարանների նախագահների խորհրդի N17-Ա որոշմամբ սահմանված հուշանվերը` "Վճռաբեկ դատարանի հիմնադրման 10-ամյակ" հուշադրամը և Վան Շենցզյունին ներկայացրեց Հայաստանի Հանրապետություն փոխայցելության առաջարկ, որը շնորհակալությամբ ընդունվեց:   <br /> <br />ՀՀ վճռաբեկ դատարանի նախագահ Արման Մկրտումյանի պատվին ՉԺՀ  Գերագույն ժողովրդական դատարանի նախագահ Վան Շենցզյունի անունից տրվեց պաշտոնական  ընթրիք:</p><p align="justify">Այնուհետև պատվիրակությունը Պեկին և Շանհայ քաղաքներում այցելեց տարբեր ատյանի դատարաններ, ծանոթացավ նրանց աշխատանքին: Պատվիրակության անդամներին առանձնապես հետաքրքրեց առաջին ատյանի դատարաններում քաղաքացիական գործերով հաշտարար դատարանների առկայությունը, որոնց աշխատանքի արդյունքում քաղաքացիական գործերի շուրջ 30 տոկոսը լուծվում է հաշտության համաձայնությամբ:</p><p align="justify">Չինաստանի դատարաններն աչքի էին ընկնում տեխնիկական բարձր հագեցվածությամբ, ինչը նպաստում էր արդարադատության իրականացման արդյունավետության բարձրացմանը:</p><p align="right"><br /><strong>Ամալյա ՀԱՐՈՒԹՅՈՒՆՅԱՆ</strong></p>', 1, 1, 0, 35, '2011-11-09 06:46:32', 64, '', '0000-00-00 00:00:00', 0, 64, '2011-11-09 11:45:03', '2011-11-09 06:46:32', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 79, '', '', 0, 4978, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES(264, 'ՄԱՐՏԻ 8', '-8', '', '<p align="justify"><img src="images/stories/onlyforrry-1.gif" border="0" hspace="5" vspace="5" align="left" />ՀՀ դատավորների միությունը շնորհավորում է բոլոր կին դատավորներին, ինչպես նաև մայրերին ու քույրերին Կանանց տոնի` Մարտի 8-ի կապակցությամբ, մաղթում նրանց քաջառողջություն, կանացի անսպառ հմայք, խաղաղ երկինք, անձնական և ընտանեկան երջանկություն: </p>', '', 1, 1, 0, 35, '2012-03-06 12:09:21', 64, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2012-03-06 12:09:21', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 76, '', '', 0, 3949, 'robots=\nauthor=');
