INSERT INTO `jos_content` VALUES(286, 'ՀԱՅՏԱՐԱՐՈՒԹՅՈՒՆ', '2012-10-23-11-16-46', '', '<p align="justify"><img src="images/stories/coeint.jpg" border="0" hspace="7" vspace="7" align="left" />Հարգելի դատավորներ, դատական ծառայողներ և իրավաբաններ, առաջարկում ենք ծանոթանալ Եվրոպայի խորհրդի շրջանակներում իրավաբանների համար մարդու իրավունքների ուսուցման վերաբերյալ նոր, թարմացված ինտերնետային կայքին <a href="http://www.coe.int/help">www.coe.int/help</a>  հասցեով, որտեղ զետեղված են ԵԽ անդամ 47 երկրների, այդ թվում` Հայաստանի Հանրապետության կողմից տրամադրված իրավաբանական, ուսուցողական բնույթի նյութեր, որոնք վերաբերում են Եվրոպական կոնվենցիային և Մարդու իրավունքների եվրոպական դատարանի պրակտիկային: Կայքում կարող եք գտնել նաև հայերեն լեզվով, մշտապես թարմացվող նյութեր:</p><p align="justify">Հարցեր առաջանալու դեպքում կարող եք դիմել help ինտերնետային կայքի հայաստանյան կոորդինատոր, ՀՀ դատավորների միության գործադիր տնօրեն, ԵՊՀ դոցենտ, ՀՀ ԳԱԱ ավագ գիտաշխատող <strong>Վահե Ենգիբարյանին</strong> (հեռ. 56-99-79/ e-mail: <a href="mailto:judge@netsys.am">judge@netsys.am</a>): </p>', '', 1, 1, 0, 35, '2012-10-23 11:13:05', 64, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2012-10-23 11:13:05', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=hy-AM\nkeyref=\nreadmore=', 1, 0, 58, '', '', 0, 4470, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES(287, 'ՀՀ ՀԱՏՈՒԿ ՔՆՆՉԱԿԱՆ ԾԱՌԱՅՈՒԹՅԱՆ ԿԱՐԳԱՎԻՃԱԿԸ, ԳՈՐԾՈՒՆԵՈՒԹՅԱՆ ԱՌԱՆՁՆԱՀԱՏԿՈՒԹՅՈՒՆՆԵՐԸ ԵՎ ԿԱՏԱՐԵԼԱԳՈՐԾՄԱՆ ՀԵՌԱՆԿԱՐՆԵՐԸ', '2012-11-14-13-16-17', '', '<p><a href="images/Publications/Am/hqc.pdf">ՀՀ ՀԱՏՈՒԿ ՔՆՆՉԱԿԱՆ ԾԱՌԱՅՈՒԹՅԱՆ ԿԱՐԳԱՎԻՃԱԿԸ, ԳՈՐԾՈՒՆԵՈՒԹՅԱՆ ԱՌԱՆՁՆԱՀԱՏԿՈՒԹՅՈՒՆՆԵՐԸ ԵՎ ԿԱՏԱՐԵԼԱԳՈՐԾՄԱՆ ՀԵՌԱՆԿԱՐՆԵՐԸ</a></p><p>Գիտագործնական վերլուծություն</p><p>Հեղինակ` <strong>Վահե ԵՆԳԻԲԱՐՅԱՆ</strong> - ՀՀ դատավորների միության գործադիր տնօրեն, ԵՊՀ դոցենտ, ՀՀ ԳԱԱ ավագ գիտաշխատող, իրավագիտության թեկնածու, դոցենտ</p><p align="justify">Հետազոտությունը նվիրված է ՀՀ հատուկ քննչական ծառայության կարգավիճակի և գործառույթների առանձնահատկությունների, ինչպես նաև վատ վերաբերմունքի և անպատժելիության դեմ պայքարի ոլորտում վերջինիս  դերի բացահայտմանը: Աշխատանքում վերլուծվել են ՀՀ հատուկ քննչական ծառայությանը վերաբերող օրենսդրությունը, համակարգը, ինչպես նաև գործառույթները: </p><p align="justify">Վերլուծությունը կարող է օգտակար լինել նաև իրավաբանական բուհերի ուսանողների և իրավունքի բնագավառում գիտական հետազոտություններ կատարող անձանց համար:</p>', '', 0, 1, 0, 36, '2012-11-14 13:07:27', 64, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2012-11-14 13:07:27', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 18, '', '', 0, 0, 'robots=\nauthor=');
