INSERT INTO `jos_content` VALUES(310, 'Կառավարությունը չի արձագանքել դատավորների դժգոհությանը', '2013-10-30-07-44-53', '', '<div class="newsContent"><p><img src="http://www.court.am/upload/image/Court/Sargsyan.jpg" border="0" hspace="5" width="247" height="200" align="right" />Պետական պաշտոններ զբաղեցնող անձանց վարձատրության մասին» օրինագիծը, որը մշակել է կառավարության աշխատակազմը, ԱԺ-ում քննարկման փուլում է, իսկ  արդարադատության նախարարության առաջարկած «ՀՀ դատական օրենսգրքում փոփոխություններ եւ լրացումներ կատարելու մասին» օրենքի նախագիծն այժմ թարգմանվում է, որպեսզի ուղարկվի Վենետիկի հանձնաժողով:</p><p> Օրերս նոր օրինագծերը հնչեղ քննադատութան ենթարկվեցին օմբուդսմենի եւ ՀՀ դատավորների միության կողմից: Վերջինը նույնիսկ հայտարարություն տարածեց, որում պաշտոնյաների աշխատավարձերին վերաբերող օրինագիծը բնորոշեց որպես դատավորների իրավական վիճակը վատթարացնող օրենք: Իսկ օմբուդսմեն Կարեն Անդրեասյանը, որ հազվադեպ է բացասական գնահատականներ տալիս արդարադատության նախարարության գործունեությանը, Դատական օրենսգրքի փոփոխությունների նախագիծը որակեց որպես «ավելի վատ կամ նույնքան վատ օրենք՝ շատ վատի փոխարեն»:</p><p>«Հրապարակի» հարցերին պատասխանեց Հայաստանի դատավորների միության նախագահ Հրաչիկ Սարգսյանը:</p><p>&nbsp;</p>', '<p><strong>- Ի՞նչ հաշվարկի են դեմ դատավորները, ինչո՞ւ են համարում, որ «Պետական պաշտոններ զբաղեցնող անձանց վարձատրության մասին» օրինագիծն իրենց իրավական վիճակը վատթարացնում է:</strong></p><p>&nbsp;</p><p>- Օրինագծով նախատեսվում է դատավորներին հավելավճարների վճարման այնպիսի կարգ, որի համաձայն, դրանք կազմելու են տարեկան 2 տոկոսի չափով, իսկ որպես դրա առավելագույն սահման՝ նախատեսված է դատավորի հիմնական աշխատավարձի 30 տոկոսը: Այսինքն, ըստ առաջարկվող տարբերակի, դատավորների հավելավճարը սահմանվում է առավելագույնը 15 տարի ժամկետով. յուրաքանչյուր տարի՝ 2 տոկոսի չափով: Մինչդեռ, գործող կարգավորմամբ, դատավորների հավելավճար ստանալու իրավունքը նախ որեւէ ժամկետային սահմանափակմամբ պայմանավորված չէ, ավելին` 2 տոկոս սահմանված է միայն առաջին 5 տարիների համար, իսկ 6-րդ եւ դրան հաջորդող յուրաքանչյուր տարվա համար սահմանված է 5-ական տոկոսի չափով հավելավճար ստանալու իրավունք:</p><p>&nbsp;</p><p>Կարգավորման վատթարացնող բնույթն առավել ակնհայտ է դառնում այն հանգամանքի հաշվառմամբ, որ այս պահի դրությամբ ՀՀ-ում պաշտոնավարող դատավորներից 27 դատավոր ունի 6-10 տարվա աշխատանքային ստաժ, 68 դատավոր` 11-15 տարվա, իսկ 61 դատավոր` 16-ից բարձր աշխատանքային ստաժ: Նշված դատավորների յուրաքանչյուր տարվա համար 5 տոկոս հավելավճար ստանալու իրավունքը ծագել է, եւ այդ հավելավճարներն իրենց պաշտոնավարման ընթացքում չփոփոխելու նորմ-երաշխիքի ուժով (ՀՀ դատական օրենսգիրք, հոդված 75, մաս 3) նրանք գտնվում են օրենսդիրի կողմից հստակ կանխորոշված ՈՐՈՇԱԿԻ իրավական վիճակում: Հետեւաբար, նախագծով նախատեսված սահմանափակումներն ուղղակիորեն նրանց իրավական վիճակը վատթարացնող նորմեր են:  </p><p>-<strong> Որքա՞ն է կազմում նրանց աշխատավարձը հիմա:</strong></p><p><strong> </strong></p><p>- Պատասխանեմ օրինակների միջոցով: Գործող իրավակարգավորմամբ՝ 7 տարվա աշխատանքային ստաժ ունեցող դատավորն ստանում է հավելավճար իր պաշտոնական դրույքաչափի 20 տոկոսի չափով, իսկ օրինագծի կարգավորման դեպքում կստանա 14 տոկոս հավելավճար: 11 տարվա ստաժ ունեցող դատավորը, ով ստանում է 40 տոկոսի չափով հավելավճար, կստանա 22 տոկոս հավելավճար: 16 տարվա աշխատանքային ստաժ ունեցողը, ով ստանում է 65 տոկոս հավելավճար, կստանա 30 տոկոս:  Ընդ որում՝ առկա կարգավորման դեպքում, ի տարբերություն օրինագծի, ժամկետային սահմանափակում չի նախատեսվում հավելավճարների չափի աճման առումով (0-5 տարի՝ 2 տոկոս, 6-րդ տարվանից սկսած յուրաքանչյուր տարի` 5 տոկոս): Մինչդեռ, ըստ օրինագծի, հավելավճարների աճը հնարավոր է միայն 15 տարիների ընթացքում, յուրաքանչյուր տարի՝ 2 տոկոսի չափով:</p><p>&nbsp;</p><p><strong>- Ո՞ւմ էր ուղղված Դատավորների միության խորհրդի հայտարարությունը: Արձագանքե՞լ է արդյոք արդարադատության նախարարությունը միության հայտարարության մեջ շարադրված պահանջներին: Կա՞ն այլ մարմիններից արձագանքներ:</strong></p><p>&nbsp;</p><p>- Դատավորների միության խորհրդի հայտարարությունն ուղղված էր բոլոր իրավասու մարմիններին, իսկ արձագանքի մասին մենք տեղեկանում ենք մամուլի հրապարակումների միջոցով: Մասնավորապես, մամուլի հրապարակումներից տեղեկացանք, որ դատավորների գնահատմանը եւ կարգապահական հիմքերին վերաբերող նախագիծն ուղարկվել է միջազգային փորձաքննության:</p><p>&nbsp;</p><p><strong>- Նախագծերը՝ նախքան ԱԺ օրակարգ մտնելը, քննարկվե՞լ են Դատավորների միության հետ, թե՞ ոչ:</strong></p><p>&nbsp;</p><p>- Ոչ, չեն քննարկվել, ի հեճուկս Եվրոպայի խորհրդի կողմից 1998 թվականին ընդունված «Դատավորների կարգավիճակի մասին» խարտիայի 1.8-րդ կետի այն պահանջի, որ դատավորների վերաբերյալ օրենքի փոփոխման ծրագրերի եւ պարգեւատրման ու սոցիալական ապահովման սահմանման կապակցությամբ դատավորների հետ պետք է իրականացվեն խորհրդակցություններ:</p><p>&nbsp;</p><p>-<strong> Ինչպե՞ս եք մեկնաբանում ՄԻ պաշտպան Կարեն Անդրեասյանի գնահատականը, որ առաջարկվող փոփոխությունները տարբերակված մոտեցում են ենթադրում վճռաբեկի դատավորների եւ նախագահի նկատմամբ, քանի որ բացառում են նրանց պատասխանատվության ենթարկելը նույն հիմքերով, որոնցով պատասխանատվության պետք է ենթարկվեն  առաջին ատյանի դատավորները:</strong></p><p><strong> </strong></p><p>- ՄԻ պաշտպան Կարեն Անդրեասյանի գնահատականը մեկնաբանելուց ձեռնպահ կմնանք` դատավորների վարքագծի կանոնների տեսանկյունից համապատասխան իրավասություն չունենալով:</p></div>', 1, 1, 0, 35, '2013-10-30 07:41:33', 64, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2013-10-30 07:41:33', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 43, '', '', 0, 5546, 'robots=\nauthor=');
