INSERT INTO `jos_content` VALUES(319, 'Հայաստանում Մարդու իրավունքների եվրոպական չափանիշների միատեսակ կիրառմանն աջակցող աշխատանքային խմբի առաջին հանդիպում', '2014-05-06-12-51-56', '', '<p align="justify">Ս.թ. ապրիլի 28-ին և 29-ին տեղի ունեցավ Հայաստանում Մարդու իրավունքների եվրոպական չափանիշների միատեսակ կիրառմանն աջակցող աշխատանքային խմբի 1-ին հանդիպումը: Աշխատանքային խմբի ստեղծումը իրականցվել է «Մարդու իրավունքների եվրոպական կոնվենցիայի և Մարդու իրավունքների եվրոպական դատարանի նախադեպային իրավունքի կիրառման ամրապնդումը Հայաստանումե  ծրագրի շրջանակներում, որն իրականացնում է Դանիայի կառավարության ֆինանսավորմամբ: Աշխատանքային խմբի   կազմում ընդգրկված էին Արդարադատության նախարարության, Արդարադատության ակադեմիայի, Վճռաբեկ դատարանի, վերաքննիչ դատարանների, ընդհանուր իրավասության դատարանների և Դատական դեպարտամենտի ներկայացուցիչները: </p><p align="justify">Աշխատանքային խմբի անդամները քննարկել են փետրվարի 24-ից 27-ը կայացած կարիքների գնահատման առաքելության արդյունքում կազմված հաշվետվության նախագիծը և առաջարկությունների փաթեթը: Կարիքների գնահատման առաջին առաքելության նպատակն էր ուսումնասիրել ներկայիս գործելակերպը, բացահայտել ներպետական դատավճիռների անհամապատասխանությունները ՄԻԵԴ դրույթներին և ՄԻԵԴ նախադեպային իրավունքին, ինչպես նաև մարդու իրավունքների եվրոպական չափանիշների միատեսակ կիրառմանը խոչընդոտող խնդիրները: Աշխատանքային խմբի հանդիպման ընթացքում քննարկվեցին կարիքների գնահատման հաշվետվության նախագիծը և համապատասխան առաջարկությունները` ուղղված մարդու իրավունքների ինստիտուտի ստեղծման, Վճռաբեկ դատարանի որոշումների կոդավորման և առցանց բազայի հետագա զարգացման հիմնահարցերին: Ի հավելումն, նախանշվեցին Վճռաբեկ դատարանի դատավորների և դատական ծառայողների վերապատրաստման կարիքները, ինչպես նաև քննարկվեց Մարդու իրավունքների եվրոպական չափանիշների միատեսակ կիրառման բարելավմանն ուղղված գործողությունների ծրագիրը: </p>', '', 1, 1, 0, 35, '2014-05-06 12:46:42', 64, '', '2014-05-06 12:55:25', 64, 0, '0000-00-00 00:00:00', '2014-05-06 12:46:42', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 41, '', '', 0, 3128, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES(318, 'ՀԱՅԱՍՏԱՆԻ ՀԱՆՐԱՊԵՏՈՒԹՅԱՆ ՎՃՌԱԲԵԿ ԴԱՏԱՐԱՆԻ ՈՐՈՇՈՒՄՆԵՐԻ ԳԻՏԱԳՈՐԾՆԱԿԱՆ ՄԵԿՆԱԲԱՆՈՒԹՅՈՒՆՆԵՐ. ՀԱՏՈՐ 2', '--------2', '', '<img src="images/stories/vch14.jpg" border="0" hspace="7" vspace="7" align="left" /><a href="images/Publications/Am/vch14.pdf">Հայաստանի Հանրապետության վճռաբեկ դատարանի որոշումների գիտագործնական մեկնաբանություններ. Հատոր 2</a>', '', 1, 1, 0, 36, '2014-04-02 07:17:41', 64, '', '0000-00-00 00:00:00', 0, 64, '2016-11-28 10:49:48', '2014-04-02 07:17:41', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 9, '', '', 0, 2386, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES(316, 'ՍՈՑԻԱԼԱԿԱՆ ԻՐԱՎՈՒՆՔՆԵՐԻ ՇՏԵՄԱՐԱՆ', '2014-02-06-06-21-14', '', '<p><a href="images/Publications/Am/socirav.pdf"><img src="images/stories/socirav.jpg" border="0" hspace="7" vspace="7" align="right" />ՍՈՑԻԱԼԱԿԱՆ ԻՐԱՎՈՒՆՔՆԵՐԻ ՇՏԵՄԱՐԱՆ</a></p><p><strong>ԱՐԹՈՒՐ ՉԱԽՈՅԱՆ <br />ԳՈՌ ԳՐԻԳՈՐՅԱՆ</strong></p><p>Խմբագիրներ`  <strong>Հրաչիկ ՍԱՐԳՍՅԱՆ</strong> - Արմավիրի մարզի ընդհանուր  իրավասության առաջին ատյանի դատարանի նախագահ, <br />ՀՀ դատավորների միության նախագահ</p><p><strong>Վահե ԵՆԳԻԲԱՐՅԱՆ</strong> - ՀՀ դատավորների միության գործադիր տնօրեն,<br />իրավագիտության թեկնածու, դոցենտ<br /> <br />Լեզվաոճական խմբագիր՝  <strong>Ամալյա ՀԱՐՈՒԹՅՈՒՆՅԱՆ</strong></p><p>Ձեռնարկում համակարգված ձևով վերլուծվել և ներկայացվել են սոցիալական իրավունքին վերաբերող առանցքային հիմնահարցերը, ինչպես նաև այս բնագավառում ՀՀ օրենսդրության հիմնական դրույթներն ու դատական պրակտիկան: <br />Աշխատանքը նպատակաուղղված է քաղաքացիներին, ինչպես նաև շահագրգիռ այլ անձանց ու կազմակերպություններին ներկայացնելու Հայաստանի Հանրապետությունում սոցիալական իրավունքի բնագավառում առկա վիճակը և իրավակիրառ պրակտիկայի զարգացման ուղղություններն ու հեռանկարները:<br />Այն կարող է օգտակար լինել ինչպես պրակտիկ իրավաբանների՝ դատավորների, դատախազների, փաստաբանների և իրավակիրառ գործունեություն իրականացնող մյուս մարմինների, այնպես էլ իրավաբանական բուհերի ուսանողների, իրավունքի բնագավառում գիտական հետազոտություններ կատարող անձանց համար:</p><p><strong>ARTUR CHAKHOYAN<br />GOR GRIGORYAN</strong></p><p><strong>DATABASE OF SOCIAL RIGHTS<br /></strong>FAMILY LAW MANUAL </p><p>Editors: <strong>Hrachik SARGSYAN</strong> - Chairman of Armavir marz general <br />jurisdiction court of the RA, <br />President of the Association of Judges of the RA</p><p><strong>Vahe YENGIBARYAN</strong> - Executive Director of the Association <br />of Judges of the RA, <br />Professor of YSU Law Faculty, PhD in law, Associated Professor of Jurisprudence</p><p>Lingvo-stylistic editor: <strong>Amalya HARUTYUNYAN</strong></p>', '', 1, 1, 0, 36, '2014-02-06 06:11:24', 64, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2014-02-06 06:11:24', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 10, '', '', 0, 2447, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES(317, 'ՀԱՂՈՐԴԱԳՐՈՒԹՅՈՒՆ', '2014-02-12-07-26-06', '', 'ՍԴ-ն այսօր (փետրվարի 11-ին)  կայացրել է որոշում, որով վարույթ է ընդունել պաշտոնաթող դատավոր Սուրիկ Ղազարյանի անունից ներկայացված դիմումը։ Դիմումով նրա ներկայացուցիչներ Արտակ Զեյնալյանը և Արա Ղազարյանը վիճարկում էին օրենքի հետադարձ կիրառությունը, որի արդյունքում դատավորը կրել էր գույքային էական վնաս՝ գրեթե ամբողջությամբ զրկվել էր իր կենսաթոշակից, որը նա վաստակել էր տարիների իր աշխատանքով։ Հաշվի առնելով այն հանգամանքը, որ մոտ երկու շաբաթ առաջ էլ ՄԻԵԴ-ն էր կայացրել որոշում, որով որոշել էր մեկ այլ՝ պաշտոնաթող չորս դատավորների անունից ներկայացված դիմումներին տալ առաջնահերթություն ու գործը քննել արագացված կարգով, որով կրկին վիճարկվում էր նույն օրենքի (ՀՕ-141-Ն) նույն հոդվածի մեկ այլ բաղադրիչի հետադարձ կիրառությունը, որի արդյունքում դատավորները հայտնվել էին խիստ անհամաչափ գույքային բեռի տակ, կարելի է հուսալ, որ լուրջ նախադրյալներ կան, ըստ որոնց Գործադիրը վերջապես կվերանայի իր սոցիալական քաղաքականությունը դատավորների նկատմամբ, որով լրջորեն հարվածի տակ է դրվում դատական համակարգի անկախությունը»։<br />', '', 1, 1, 0, 35, '2014-02-12 07:24:10', 64, '', '0000-00-00 00:00:00', 0, 64, '2014-03-12 07:12:08', '2014-02-12 07:24:10', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 42, '', '', 0, 2461, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES(329, 'ՀԱՄԱՏԵՂ ԱՇԽԱՏԱՆՔԱՅԻՆ ՊԼԱՆ', '2014-12-25-11-57-04', '', 'ՀՀ դատավորների միությունը ABA CEELI Inc-ի համատեղ &lt;&lt;Դատական իշխանության ինստիտուցիոնալ զարգացման հեռանկարները&gt;&gt; թեմայով ծրագրի արդյունքում մշակված <a href="images/Publications/Am/actionplan.pdf">աշխատանքային պլան</a>', '', 1, 1, 0, 35, '2014-12-25 11:52:55', 64, '', '2014-12-25 11:57:29', 64, 0, '0000-00-00 00:00:00', '2014-12-25 11:52:55', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 33, '', '', 0, 2142, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES(330, 'ԴԱՍ ՎԵՑԵՐՈՐԴ', '2014-12-25-12-08-13', '', '<p><a href="images/Publications/Am/1.pdf">Ազատությունից զրկում. հիմնավոր կասկած</a></p><p><a href="images/Publications/Am/2.pdf">Կալանքի համար անհրաժեշտ է</a></p><p><a href="images/Publications/Am/3.pdf">Խափանման միջոցի ընտրության դատական նիստի անցկացում</a></p><p><a href="images/Publications/Am/4.pdf">Խափանման միջոցի ընտրության դատական որոշումների կայացում </a></p><p><a href="images/Publications/Am/5.pdf">Հիմնավոր կասկած, փախուստի և գործի քննությանը խոչընդոտելու վտանգ</a></p>', '', 1, 1, 0, 35, '2014-12-25 11:58:32', 64, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2014-12-25 11:58:32', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 32, '', '', 0, 2122, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES(331, 'ՀԱՄԱԺՈՂՈՎ ԿԻՆ ԴԱՏԱՎՈՐՆԵՐԻ ՀԱՄԱՐ', '2015-02-26-13-38-56', '', 'Մարտի 27-29-ը Թունիսում կին դատավորների համար տեղի կունենա համաժողով: Համաժողովին մասնակցել ցանկացողների համար կից ներկայացնում ենք համաժողովի <a href="images/conferencetunis.pdf">օրակարգը:</a>', '', 1, 1, 0, 35, '2015-02-26 13:32:44', 64, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2015-02-26 13:32:44', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 31, '', '', 0, 1964, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES(332, 'ՍԵՄԻՆԱՐ ԴԱՆԻԱՅՈՒՄ', '2015-03-05-11-05-11', '', '<p>Հարգելի բարեկամներ և գործընկերներ,</p><p>Կոպենհագենում Դանիայի Խոշտանգումների դեմ ինստիտուտում տեղի կունենա Մարդու իրավունքների փաստաթղթերի վերաբերյալ Եվրոպական համակարգման հանձնաժողովի (ECCHRD) 2015թ. հանդիպումը: Հանդիպումը տեղի կունենա մայիսի 7-8:<br />Սույն հանդիպումը հիանալի հնարավորություն է ներկայացնել ձեր գործը, և ծրագրերը: Մասնակցության համար խնդրում ենք տեղեկացնել մարտին:</p><p>Հյուրանոցի և  ճանապարհորդության հետ կապված բոլոր ծախսերը հոգում է մասնակցող կողմը:</p>', '', 1, 1, 0, 35, '2015-03-05 11:02:23', 64, '', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2015-03-05 11:02:23', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 30, '', '', 0, 1974, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES(333, 'ԴԱՏԱԿԱՆ ՓՈՐՁԱՔՆՆՈՒԹՅՈՒՆՆԵՐԻ ՔՐԵԱԴԱՏԱՎԱՐԱԿԱՆ ԵՎ ԿՐԻՄԻՆԱԼԻՍՏԻԿԱԿԱՆ ՀԻՄՆԱԽՆԴԻՐՆԵՐԸ', '2015-03-27-07-16-12', '', '<p><a href="images/Publications/Am/5/dzernak aba.pdf">ԴԱՏԱԿԱՆ ՓՈՐՁԱՔՆՆՈՒԹՅՈՒՆՆԵՐԻ ՔՐԵԱԴԱՏԱՎԱՐԱԿԱՆ ԵՎ ԿՐԻՄԻՆԱԼԻՍՏԻԿԱԿԱՆ ՀԻՄՆԱԽՆԴԻՐՆԵՐԸ. ՀԱՄԵՄԱՏԱԿԱՆ ԻՐԱՎԱԿԱՆ ՎԵՐԼՈՒԾՈՒԹՅՈՒՆ</a></p><p><strong>ՎԱՀԵ ԵՆԳԻԲԱՐՅԱՆ</strong></p><p align="justify">Աշխատանքը նվիրված է դատական փորձաքննությունների  տեսության և պրակտիկայի  հիմնախնդիրներին: Հիմնականում ներկայացվում են դատական փորձաքննություններին, փորձագետի դատավարական կարգավիճակին, ինչպես նաև փորձագետի եզրակացությանը վերաբերող հարցերը և այլն:<br />Աշխատանքը կարող է օգտակար լինել փորձագետների, քննիչների, փաստաբանների, դատավորների, դատախազների, իրավաբանական բուհերի ուսանողներին, դասախոսների, ասպիրանտների և դատական փորձաքննությունների հիմնախնդիրներով հետաքրքրվող անձանց:</p><p align="justify"><img src="images/stories/aba2.jpg" border="0" />              <img src="images/stories/aba3.jpg" border="0" /></p><p align="justify">Աշխատանքը պատրաստվել է Ամերիկյան իրավաբանների ընկերակցության &lt;&lt;Իրավունքի գերակայության նախաձեռնություն&gt;&gt; ծրագրի սահմաններում: </p>', '', 1, 1, 0, 36, '2015-03-27 07:14:00', 64, '', '2015-12-22 10:20:29', 64, 0, '0000-00-00 00:00:00', '2015-03-27 07:14:00', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 4, 0, 6, '', '', 0, 1215, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES(334, 'ԴԱՏԱԿԱՆ ՈԼՈՐՏՈՒՄ ԱՆԵԼԻՔՆԵՐԸ ԴԵՌ ՇԱՏ ԵՆ', '2015-03-29-07-27-01', '', '<div>Օրերս տեղի ունեցավ դատավորների ընդհանուր ժողով, որի ընթացքում, ի թիվս այլ հարցերի, ՀՀ վճռաբեկ դատարանի քրեական պալատի նախագահ<strong> Դավիթ Ավետիսյանը</strong> ներկայացրեց 2014 թվականի վիճակագրական տվյալները։</div><div style="text-align: justify">Նա մասնավորապես նշեց, որ 2014 թվականին քրեական վարույթի շրջանակներում ստացվել է 21318 գործ, որից 4021—ն ըստ էության քրեական գործեր են, 17297—ը՝ մինչդատական վարույթի նկատմամբ դատական վերահսկողությանը եւ դատական ակտերի կատարմանը վերաբերող միջնորդություններ։ Ստացված 4021 քրեական գործից ավարտվել է 2882—ը (71.7%—ը)։ Ստացված 17.297 միջնորդությունից ավարտվել է 17.205—ը (99.5%—ը)։ Ընդհանուր առմամբ, ավարտվել է 20.087 գործ կամ գործերի 94.2%–ը։</div><div style="text-align: justify">Չնայած վերջին շրջանում նկատվող նվազման միտումներին՝ նշանակվող պատիժների շարքում ազատությունից զրկելու հետ կապված պատժատեսակների (ազատազրկում, կալանք, կարգապահական գումարտակում պահել) կիրառումը շարունակում է գերակշռել։ Այսպես՝ եթե 2011թ. ռեալ ազատազրկման է դատապարտվել դատապարտվածների 68%—ը, 2012թ.՝ 59%—ը, 2013թ.՝ 56%—ը, ապա 2014—ին այդ ցուցանիշը կազմում է շուրջ 50%։</div><div>Այսպիսով՝ նվազման միտումն առկա է, սակայն իրավիճակը դեռ համահունչ չէ Եվրախորհրդի անդամ պետությունների պրակտիկային եւ միջին ցուցանիշին։</div><div>Իրավիճակի պատճառները տարբեր են, սակայն, ըստ Դ. Ավետիսյանի, դրանցից գլխավորը կոռուպցիայի դեմ ակտիվ պայքարի իմիտացիան է, որը վերաբերելի է ինչպես իրավապահ ողջ համակարգին, այնպես էլ դատական համակարգին։</div>', '<div><strong>Դ. Ավետիսյանի</strong> տեղեկացմամբ՝ անցյալ տարի քրեական վարույթի շրջանակներում քննված 20.087 գործերով վերաքննիչ քրեական դատարան բերվել է 2850 բողոք, որոնցով բողոքարկվել է կայացված դատական ակտերի 12.7%—ը։</div><div>Ըստ էության քրեական գործերով բողոքարկվել է 1156 դատական ակտ, որոնցով վարույթն ավարտվել է 1033—ով (շուրջ 90%—ով)։ Բեկանվել է 264 դատական ակտ, որը կազմում է բողոքարկված դատական ակտերի 25.5%—ը։ Ստացվում է, որ ըստ էության քրեական գործերով բողոքարկված յուրաքանչյուր 4—րդ դատական ակտ բեկանվել է, ինչը մտահոգիչ ցուցանիշ է։</div><div>Դատական վերահսկողության վարույթի շրջանակներում բերված 1445 բողոքներից բավարարվել է 137—ը կամ 9.5%—ը։</div><div>Ընդհանուր առմամբ, բողոքարկված 2563 դատական ակտերից բեկանվել են 403—ը, որը կազմում է 15.7%—ը։</div><div style="text-align: justify">Դրական պետք է համարել, որ 2014թ. քրեական գործերով բողոքների թիվը պակասել է շուրջ 10%—ով, իսկ մինչդատական վարույթում պաշտոնատար անձանց գործողությունների դեմ բերված բողոքները աճել են, այն էլ 19%—ով, ինչը նշանակում է, որ քաղաքացիները մինչդատական վարույթում իրենց իրավունքները պաշտպանելու համար սկսել են ավելի հաճախ օգտվել դատական պաշտպանությունից։ Վերջինս կարող է վկայել դատարանների հեղինակության աճի մասին։</div><div>ՀՀ դատարանների կողմից 2014թ. արդարացվել է 125 անձ, որից 71—ը լրիվ, 54—ը՝ մասնակի։ Նախորդ տարվա համեմատությամբ աճը կազմել է 30%։</div><div>Պատիժը պայմանականորեն չի կիրառվել 834 անձի նկատմամբ, որը կազմում է ընդհանուր դատապարտվածների շուրջ 24%—ը, ինչը նախորդ տարվանից ավելի է 3%—ով։</div><div>Նշենք, որ այս ցուցանիշով գերազանցում ենք եվրոպական միջինը, որը կազմում է 12.7%։</div><div>Ամփոփելով խոսքը՝ Դ. Ավետիսյանը նշեց, որ ինչ վերաբերում է բանկային գաղտնիքի պաշտպանությանը, ապա կատարված ուսումնասիրությունները ցույց են տալիս, որ վիճակը հեռու է բարվոք լինելուց։ Առանձնացնենք բանկային գաղտնիք առգրավելու միջնորդություններ քննարկելիս դատարանի կողմից թույլ տրվող 4 հիմնական խախտում՝ որոշումներում հիմնավորվածության բացակայություն, կասկածյալի կամ մեղադրյալի կարգավիճակի բացակայություն, տուժողի վերաբերյալ բանկային գաղտնիքի տրամադրում, բանկային գաղտնիքի տրամադրում առանց ժամկետի սահմանափակման։</div><div><br /></div><div style="text-align: right"><strong>Ամալյա ՀԱՐՈՒԹՅՈՒՆՅԱՆ</strong></div><div><a href="http://www.hhpress.am/index.php?sub=hodv&hodv=20150325_10&flag=am">http://www.hhpress.am/index.php?sub=hodv&hodv=20150325_10&flag=am</a><br /></div>', 1, 1, 0, 35, '2015-03-29 07:17:38', 64, '', '2015-03-29 07:36:45', 64, 0, '0000-00-00 00:00:00', '2015-03-29 07:17:38', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 2, 0, 29, '', '', 0, 2301, 'robots=\nauthor=');
