INSERT INTO `jos_jf_content` VALUES(55, 2, 110, 'content', 'introtext', 'Ապրիլի 15-ին ՀՀ դատավորների միության նախագահ Հրաչիկ Սարգսյանն ընդունեց Եվրոպայի խորհրդի և Եվրոպական միության պատվիրակությանը: <br />Հանդիպմանը մասնակցում էին պատվիրակության անդամներ Էրիկ Սվանիձեն և Արտաշես Մելիքյանը, ինչպես նաև ՀՀ դատավորների միության գործադիր տնօրեն Վահե Ենգիբարյանը: <br />Հանդիպման ընթացքում հյուրերը բարձր գնահատեցին ՀՀ դատավորների միության և Եվրոպայի խորհրդի հետ ավելի քան 8-ամյա համագործակցության արդյունքները, քննարկվեցին Եվրոպայի խորհրդի և Եվրոպական միության հետ համագործակցությունը սերտացնելու, համատեղ ծրագրեր իրականացնելու անհրաժեշտության մասին, մասնավորապես` դատավորների համար Եվրոպական կոնվենցիայի 3-րդ հոդվածի վերաբերյալ դասընթացների կազմակերպման, ինչպես նաև այդ բնագավառում Եվրոպական դատարանի մոտեցումներին և նախադեպային պրակտիկային ծանոթացնելու վերաբերյալ հարցեր:  <br />', 'fb12dae09079da68608171de43a12e48', '', '2009-05-21 18:00:50', 62, 1);
