INSERT INTO `jos_jf_content` VALUES(59, 2, 113, 'content', 'introtext', 'Ղեկավարվելով Հայաստանի Հանրապետության Սահմանադրության 55-րդ հոդվածի 11-րդ կետով և հիմք ընդունելով Արդարադատության խորհրդի 2009 թվականի ապրիլի 23-ի "Հայաստանի Հանրապետության վերաքննիչ քաղաքացիական դատարանի դատավոր նշանակելու համար թեկնածություն ներկայացնելու վերաբերյալ" եզրակացությունը` ՀՀ նախագահ Սերժ Սարգսյանը մայիսի 4-ի իր հրամանագրով Ամրահի ՊԵՏՐՈՍՅԱՆԻՆ նշանակել է Հայաստանի Հանրապետության վերաքննիչ քաղաքացիական դատարանի դատավոր` դադարեցնելով նրա` Երևան քաղաքի Էրեբունի և Նուբարաշեն համայնքների ընդհանուր իրավասության դատարանի դատավորի լիազորությունները:', '4a3d9851e330587024b188ee0824ecc9', '', '2009-05-21 18:05:02', 62, 1);
