INSERT INTO `jos_jf_content` VALUES(62, 2, 112, 'content', 'introtext', 'ՀՀ դատավորների միությունը Եկեղեցիների համաշխարհային խորհրդի հայաստանյան միջեկեղեցական բարեգործական Կլոր սեղան հիմնադրամի աջակցությամբ ձեռնամուխ է եղել "Դատարան և արդարադատություն" և "Եկեղեցի, պետություն, հասարակություն" վերտառությունները կրող ձեռնարկների հրատարակմանը: Նշված ձեռնարկներն իրավաբանորեն մատչելի ձևակերպված հարց ու պատասխանի ձևով կներառեն ՀՀ բնակչությանն առավել հուզող նշված բնագավառների հիմնախնդիրների վերաբերյալ պարզաբանումներ:<br />Խնդրում ենք բոլոր ընթերցողներին, ինչպես նաև շահագրգիռ անձանց ու կազմակերպություններին առաջադրել վերը նշված թեմաներով հարցերի ձևակերպմանը, որոնց պատասխանները կցանկանայիք զետեղել նշված աշխատանքների բովանդակության մեջ: Առաջարկությունները խնդրում ենք ուղարկել գրավոր տեսքով  www.judge@netsys.am հասցեով կամ ՀՀ դատավորների միություն` Վ. Սարգսյան 5  հեռ: (+37410) 569989<br />Խմբագրական կազմի կողմից դրանք վերանայելուց հետո կարող են ներառվել համապատասխան աշխատանքների էլեկտրոնային տեքստերում:<br />', '18b3a5f331fd773829ab85ee66e50fba', '', '2009-05-21 18:05:46', 62, 1);
