INSERT INTO `jos_jf_content` VALUES(70, 2, 45, 'content', 'introtext', '<p><img border="0" hspace="15" vspace="15" align="right" />ՀՀ դատավորների միությունը հիմնադրվել է 1997թ. ապրիլին` մի խումբ դատավորների նախաձեռնությամբ: <br />1997-2003թթ. ՀՀ դատավորների միության նախագահն է եղել ՀՀ վճռաբեկ դատարանի դատավոր Ալվինա Գյուլումյանը (այժմ` Մարդու իրավունքների եվրոպական դատարանի անդամ): <br />2003 թվականից մինչ օրս ՀՀ դատավորների միության նախագահն է Արմավիրի մարզի ընդհանուր իրավասության դատարանի նախագահ Հրաչիկ Սարգսյանը: <br /><img src="images/stories/miut1.jpg" border="0" hspace="15" vspace="15" align="left" />Միության ստեղծման հետ համատեղ սկսել է հրատարակվել "Դատական իշխանություն" (նախկինում`"ՙՕրինաթերթ") գիտամեթոդական ամսագիրը: Իրավական թեքում ունեցող լրատվամիջոցներից "Դատական իշխանությունն" առաջինն էր, որն անդրադարձավ դատական պրակտիկային և առանձնացավ իր դատաիրավական ուղղվածությամբ, տպագրված նյութերի բազմազանությամբ, գիտամեթոդական խորությամբ: 2004 թվականից  "Դատական իշխանություն" ամսագիրն ընդգրկվել է թեկնածուական ատենախոսությունների արդյունքների տպագրման համար ընդունելի ամսագրերի ՀՀ բարձրագույն որակավորման հանձնաժողովի հաստատած ցանկում: <br />Ամսագիրը լույս է տեսել յուրաքանչյուր ամիս, կայուն պարբերականությամբ, 400 օրինակ տպաքանակով` իր էջերում ներառելով նոր հեղինակների: Ամսագրում հանդես են եկել ոչ միայն ճանաչված իրավաբաններ, դոկտոր պրոֆեսորներ, գիտության տարբեր ճյուղերի հանրահայտ դեմքեր, այլև բազմաթիվ շնորհալի երիտասարդներ, որոնք հավակնում են լրացնել հանրապետության իրավաբան գիտնականների շարքերը:<br />Այդ ամենը պետք է բացատրել ամսագրի` իր առջև ծառացած խնդիրների լուծման հետևողականությամբ և նպատակասլաց աշխատանքով:<br />Խմբագրական խորհրդի կազմում են իրավագիտության դոկտոր պրոֆեսորներ, այդ թվում` ՌԴ վաստակավոր իրավաբաններ, դոկտոր պրոֆեսորներ: Խմբագրական խորհուրդն ակտիվ մասնակցություն է ունենում յուրաքանչյուր համարի թեմատիկ պլանավորմանը և գիտական հոդվածների ընտրությանը, որոնք տպագրվում են համապատասխան գրախոսական-կարծիքների առկայությամբ, որը հոդվածագիրների առջև դրվող պահանջների բարձրացման հիմնավոր և անհրաժեշտ պայման է:<br />"Դատական իշխանությունը" հանրապետության եզակի ամսագրերից է, որում հանդես են գալիս առավելապես հայ դատավորներն իրենց գիտական հոդվածներով` նվիրված իրավունքի տարբեր ճյուղերի տեսական և գործնական հիմնախնդիրներին:</p>', '126cd50a925ad9f35395f54b03a0b651', '', '2010-09-16 05:55:32', 64, 1);
