INSERT INTO `jos_jf_content` VALUES(88, 2, 141, 'content', 'introtext', 'Այցը կազմակերպվել է Եվրոպայի խորհրդի կողմից և Համաշխարհային բանկի հովանավորությամբ: Այցի ընթացքում տեղի են ունեցել բազմաթիվ հանդիպումներ ու քննարկումներ Եվրոպայի խորհրդի տարբեր կառույցների ներկայացուցիչների հետ: <br />Բացման խոսքով հանդես եկան Տատյանա Թերմաչիչը (Դատական բարեփոխումներ և Ոստիկանության բաժնի գործադիր տնօրեն, Մարդու իրավունքների իրավական կառույցի վարչություն, համագործակցության տնօրինություն, Մարդու իրավունքների և իրավական հարցերի գլխավոր տնօրինություն),  <br />Եվրոպայի խորհրդում Հայաստանի Հանրապետության մշտական ներկայացուցիչ, դեսպան` Զոհրաբ Մնացականյանը, <br />ՀՀ դատավորների միության նախագահ Հրաչիկ Սարգսյանը: <br />Այնուհետև այցելության շրջանակներում տեղի ունեցան հետևյալ հանդիպումներն ու քննարկումները. <br />• ՄԻԵԴ-ի նախադեպերը և վերջինին բարեփոխումները<br />ՄԻԵԴ-ի դատավոր` Ալվինա Գյուլումյան <br />• Կոռուպցիայի դեմ պայքարի պետություների խմբի (GRECO)քարտուղարության աշխատանքները <br />Պրն Քրիսթոֆ Սփիքբեքեր (ՔԱՐՏՈՒՂԱՐՈՒԹՅԱՆ բաժնի ղեկավար (GRECO)) Մոնիթորինգի տնօրինություն, Մարդու իրավունքների և իրավական հարցերի գլխավոր տնօրինություն)   <br />• ՄԻԵԴ-ի նախադեպային պրակտիկան և վերջին զարգացումները<br />Պրն Արա Շահզադեյան ՄԻԵԴ-ի քարտուղարության իրավաբան <br />Եվրոպայի խորհրդի Մարդու իրավունքների հանձնակատարի գրասենյակ, <br />Եվրոպայի խորհրդի Մարդու իրավունքների հանձնաժողովի անդամ` տկն Գանա Յուդկիվսկա<br />• ՄԻԵԴ-ի վճիռների ի կատար ածումը<br />Տկն Շարլոտ դե Բրութելես և պրն Արտյոմ Գեղամյան (ՄԻԵԴ-ի վճիռների կատարման վարչություն, Մոնիթորինգի ղեկավարություն, Մարդու իրավունքների և իրավական հարցերի գլխավոր տնօրինություն)<br />• Իրավունքի միջոցով հանուն ժողովրդավարության Եվրոպական հանձնաժողովի աշխատանքները (Վենետիկի հանձնաժողով)  <br />Պրն Սերգեյ Կուզնեցով (Վենետիկի հանձնաժողով, Մարդու իրավունքների և իրավական հարցերի գլխավոր տնօրինություն)    <br />• Եվրոպական սոցիալական խարտիայի վերանայված տարբերակը <br />Տկն Անա Ռուսու (Եվրոպական սոցիալական խարտիայի վարչություն, Մոնիթորինգի տնօրինություն, Մարդու իրավունքների և իրավական հարցերի գլխավոր տնօրինություն)<br />• Տնտեսական հանցագծությունների բաժնի աշխատանքները   <br />Բաժնի ղեկավար` պրն Պրզեմիսլավ Մուսիալկովսկի (Տնտեսական հանցագործությունների բաժին, Հասարակական տեղեկատվության և հանցագործությունների դեմ պայքարի վարչություն, Համագործակցության բաժին, Մարդու իրավունքների և իրավական հարցերի գլխավոր տնօրինություն)<br />• Այց ՄԻԵԴ, աշխատանքային հանդիպում դատարանում: Դատարանի աշխատանքների ընթացքի և դրա աշխատանքային պայմանների հետ ծանոթացում: <br />• Այց Եվրոպայի խորհրդի հիմնական կառույց (Palais), աշխատանքային հանդիպում Եվրոպայի խորհրդի հիմնական մարմինների, ինչպես նաև Նախարարների կոմիտեի և Պառլամենտի անդամների հետ:<br />• Մասնակիցների ամփոփիչ դիտարկումներ և մեկնաբանություններ, Եվրոպայի խորհրդի և ՀՀ դատավորների միության հետագա համագործակցության վերաբերյալ քննարկումներ և առաջարկություններ: Ուսումնական այցի եզրափակում:', 'ce3175799028b0587831f900f79e701e', '', '2010-02-12 05:41:02', 64, 1);
