INSERT INTO `jos_jf_content` VALUES(105, 2, 154, 'content', 'introtext', '<p>Հարգելի գործընկերներ,</p><p> Ասերի համալսարանը սկսում է դասընթացներն Եվրոպայի խորհրդի անդամ պետությունների համար ոստիկանության և դատական համակարգի համագործակցության թեմայով, որը կընթանա Եվրոպոլի և Եվրոջասթի սերտ համագործակցությամբ: <br /> Դասընթացը կկազմակերվի 2010 թվականի մայիսի 17-ին և 18-ին Հաագայում: Դասընթացի վճարը 985 եվրո է: <br /> Դասըթացի հիմնական նպատակը գիտելիքների և փորձի փոխանակումն է, ինչպես նաև ինտերնետային ցանցի կատարելագործումն և ազատության, անվտանգության և դատական համակարգերում աշխատող անձանց միջև համագործակցության ամրապնդումը: Այս ծրագրին Եվրոպոլի և Եվրոջասթի ներդրումները նպաստում են, որպեսզի մասնակիցներն առավել խորը ընկալեն Եվրոպայի խորհրդի նշված երկու մարմինների գործունեությունը, ինչպես նաև միմյանց հետ, նաև անդամ պետությունների պաշտոնյաների և այլ պաշտոնատար անձանց հետ իրենց համագործակցության և փոխազդեցության եղանակները:<br /> Մասնակիցներին նույնպես ցանցից օգտվելու հնարավորություն կտրվի, ինչն իր հերթին կամրապնդի իրենց մասնագիտական կապերը երկու կազմակերպությունների աշխատակիցների հետ: <br /> Դասախոսությունն իր մեջ պարունակում է Ասերի համալսարանի, ինչպես նաև  Եվրոպոլի և Եվրոջասթի նիստերը: Դասընթացը միակն է իր տեսակի մեջ ոչ միայն այն պատճառով, որ այն մշակված է Եվրոպոլի և Եվրոջասթի հետ համատեղ, այլ նաև մասնակիցների համար դասընթացները կվարեն այս բնագավառի երեք բաժինների հայտնի փորձագետների կողմից: <br /> Սույն դասընթացն ուղղված է ոստիկանության պաշտոնյաներին, քաղաքացիական ծառայողներին և/կամ Արդարադատության նախարարության և Եվրոպայի խորհրդի 27 անդամ պետությունների ներպետական արդարադատության իրականացման հիմնախնդիրներին: Դասընթացի թեմաները պարունակում են Լիսաբոնյան պայմանագրի փոփոխությունների, Եվրոպոլի և Եվրոջասթի հետ համագործակցության, Եվրոպայում ձերբակալման երաշխիքների ինստիտուտի, փոխադարձ համագործակցության, համատեղ քննչական խմբերի ձևավորման և միջազգային համագործակցության ստվերային կողմի, այն է` տվյալների պաշտպանության և անձնական կյանքի վերաբերյալ հարցերը: </p><p>ՄԱՍՆԱԿԻՑՆԵՐԻ ԳՐԱՆՑՈՒՄՆ ՍԿՍՎԱԾ Է. մինչև ապրիլի 9-ը գրացվելու դեպքում կգործի 20% զեղչ:</p><p>Մանրամասների համար խնդրում ենք կապվել կրթության բաժնի վարիչ` պրն Պաթրիկ Ուիլեմսենի հետ <a href="mailto:p.willemsen@asser.nl">p.willemsen@asser.nl</a> &lt;<a href="mailto:p.willemsen@asser.nl">mailto:p.willemsen@asser.nl</a>&gt;  / 0031-(0)70 3420 329</p><p>Drs. Patrick Willemsen</p><p>Project Manager </p><p>Education Department</p><p>T.M.C. Asser Instituut</p><p>R.J. Schimmelpennincklaan 20-22</p><p>2517 JN Den Haag</p><p>T: 070 3420 329</p><p>F: 070 3420 316</p><p>E: <a href="mailto:p.willemsen@asser.nl">p.willemsen@asser.nl</a> &lt;<a href="mailto:p.willemsen@asser.nl">mailto:p.willemsen@asser.nl</a>&gt; </p><p><a href="http://www.asser.nl/">www.asser.nl</a> </p>', '80fe1a6bbd85638573b11737aaea31c2', '', '2010-03-22 05:33:51', 64, 1);
